INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('서울의 아침', '서울특별시 종로구 자하문로 10길 4 (03040)', '서울', 4, 150000, '경복궁 근처에 위치한 아늑한 한옥 독채입니다. 서울의 중심에서 고즈넉한 휴식을 즐겨보세요.', 0.0, 0),
('남산 아래, 우리집', '서울특별시 용산구 소월로 99 (04345)', '서울', 6, 220000, '남산타워가 보이는 탁 트인 전망을 자랑합니다. 가족, 친구들과 함께 특별한 추억을 만들어보세요.', 0.0, 0),
('홍대 감성 스테이', '서울특별시 마포구 와우산로29길 15 (04052)', '서울', 5, 180000, '홍대 중심가에 위치해 있어 젊음의 열기를 느낄 수 있습니다. 감각적인 인테리어와 편리한 접근성이 장점입니다.', 0.0, 0),
('강남 스타일 하우스', '서울특별시 강남구 테헤란로 112 (06134)', '서울', 8, 280000, '비즈니스와 쇼핑의 중심지 강남에 위치한 모던한 숙소입니다. 넓은 공간과 최신 시설을 갖추고 있습니다.', 0.0, 0),
('연남동 골목길', '서울특별시 마포구 동교로 245 (03981)', '서울', 4, 160000, '아기자기한 카페와 맛집이 즐비한 연남동에 자리잡고 있습니다. 조용한 골목길 안에서 여유를 만끽하세요.', 0.0, 0),
('북촌 한옥마을의 쉼', '서울특별시 종로구 계동길 37 (03059)', '서울', 3, 190000, '전통 한옥의 멋과 현대적인 편리함이 조화를 이룬 공간입니다. 고궁의 정취를 느끼며 특별한 하루를 보내세요.', 0.0, 0),
('이태원 프리덤', '서울특별시 용산구 이태원로 15길 1 (04350)', '서울', 6, 210000, '다양한 문화가 공존하는 이태원에 위치한 유니크한 숙소입니다. 루프탑에서 서울의 야경을 감상할 수 있습니다.', 0.0, 0),
('성수동 아뜰리에', '서울특별시 성동구 서울숲2길 32-14 (04768)', '서울', 4, 175000, '트렌디한 성수동 카페거리에 위치한 감성 숙소입니다. 예술적인 영감을 받으며 편안한 휴식을 취해보세요.', 0.0, 0),
('잠실 스카이뷰', '서울특별시 송파구 올림픽로 300 (05551)', '서울', 7, 250000, '롯데월드타워가 한눈에 보이는 환상적인 뷰를 자랑합니다. 석촌호수를 산책하며 낭만적인 시간을 보내세요.', 0.0, 0),
('익선동 작은 집', '서울특별시 종로구 돈화문로11나길 31-5 (03133)', '서울', 2, 140000, '레트로 감성이 가득한 익선동 한옥거리에 위치한 아담한 숙소입니다. 연인과 함께 오붓한 시간을 보내기 좋습니다.', 0.0, 0),
('문래 창작촌 쉼터', '서울특별시 영등포구 도림로128가길 13-8 (07299)', '서울', 5, 165000, '철공소와 예술이 공존하는 문래창작촌의 독특한 분위기를 느껴보세요. 옥상정원에서 바베큐 파티를 즐길 수 있습니다.', 0.0, 0),
('서촌의 오후', '서울특별시 종로구 옥인길 23-6 (03035)', '서울', 4, 185000, '인왕산 아래 조용한 서촌 마을에 위치한 단정한 주택입니다. 고즈넉한 분위기 속에서 진정한 쉼을 경험하세요.', 0.0, 0),
('신사동 가로수길 하우스', '서울특별시 강남구 도산대로13길 31-3 (06035)', '서울', 6, 230000, '패션과 문화의 거리, 가로수길에 위치한 세련된 숙소입니다. 쇼핑과 미식을 즐기기에 최적의 장소입니다.', 0.0, 0),
('망원동 리버뷰', '서울특별시 마포구 포은로 67 (04013)', '서울', 4, 195000, '한강공원이 가까워 산책이나 자전거를 즐기기 좋습니다. 창밖으로 보이는 한강의 풍경이 아름답습니다.', 0.0, 0),
('대학로 아지트', '서울특별시 종로구 동숭길 101 (03086)', '서울', 5, 155000, '연극과 공연의 중심지 대학로에 위치하고 있습니다. 문화생활을 즐기고 편안하게 쉴 수 있는 아늑한 공간입니다.', 0.0, 0),
('경리단길 루프탑', '서울특별시 용산구 회나무로 41 (04346)', '서울', 6, 240000, '남산과 이태원의 전경이 펼쳐지는 루프탑이 매력적인 숙소입니다. 친구들과 함께 잊지 못할 파티를 즐겨보세요.', 0.0, 0),
('을지로 힙플레이스', '서울특별시 중구 충무로9길 12 (04555)', '서울', 4, 170000, '오래된 건물과 트렌디한 가게들이 공존하는 을지로에 위치해 있습니다. 독특한 감성을 경험하고 싶은 분들께 추천합니다.', 0.0, 0),
('도심 속 힐링캠프', '서울특별시 서대문구 연희로11가길 42 (03702)', '서울', 8, 290000, '넓은 마당이 있어 바베큐와 캠핑 분위기를 즐길 수 있는 특별한 숙소입니다. 도심 속에서 자연을 느껴보세요.', 0.0, 0),
('삼성동 비즈니스 스테이', '서울특별시 강남구 영동대로 602 (06083)', '서울', 3, 200000, '코엑스와 가깝고 교통이 편리하여 비즈니스 출장객에게 최적입니다. 업무 후 편안한 휴식을 제공합니다.', 0.0, 0),
('해방촌 선셋 하우스', '서울특별시 용산구 신흥로 95-9 (04337)', '서울', 4, 215000, '서울의 아름다운 일몰을 감상할 수 있는 언덕 위 숙소입니다. 이국적인 분위기의 해방촌을 탐방해보세요.', 0.0, 0),
('올림픽공원 앞', '서울특별시 송파구 위례성대로 18 (05540)', '서울', 6, 225000, '올림픽공원이 바로 앞에 있어 산책과 운동을 즐기기 좋습니다. 콘서트나 행사를 관람하기에도 편리합니다.', 0.0, 0),
('인사동 전통가옥', '서울특별시 종로구 인사동10길 23-5 (03149)', '서울', 5, 198000, '한국의 전통 문화를 체험할 수 있는 인사동에 위치한 고풍스러운 한옥입니다. 툇마루에 앉아 차 한잔의 여유를 즐겨보세요.', 0.0, 0),
('여의도 리버파크', '서울특별시 영등포구 여의동로 280 (07320)', '서울', 7, 260000, '한강공원과 IFC몰이 가까워 여가와 쇼핑을 동시에 즐길 수 있습니다. 넓은 거실에서 편안하게 휴식을 취하세요.', 0.0, 0),
('상수동 카페거리', '서울특별시 마포구 독막로 67-11 (04074)', '서울', 4, 182000, '개성 넘치는 카페와 레스토랑이 많은 상수동에 위치하고 있습니다. 조용한 분위기에서 여유로운 시간을 보낼 수 있습니다.', 0.0, 0),
('동대문 쇼핑하우스', '서울특별시 중구 장충단로 247 (04561)', '서울', 8, 270000, '동대문디자인플라자(DDP)와 쇼핑몰이 인접해 있어 쇼핑을 즐기기에 완벽한 위치입니다. 밤새도록 쇼핑을 즐겨보세요.', 0.0, 0),
('압구정 럭셔리 펜트', '서울특별시 강남구 압구정로50길 29 (06018)', '서울', 6, 295000, '고급스러운 인테리어와 최상의 시설을 갖춘 럭셔리 숙소입니다. 특별한 날을 기념하기에 더없이 좋은 공간입니다.', 0.0, 0),
('창덕궁 돌담길 옆', '서울특별시 종로구 와룡동 2-71 (03070)', '서울', 3, 168000, '유네스코 세계문화유산인 창덕궁 바로 옆에 위치하고 있습니다. 고궁의 아름다운 돌담길을 따라 산책해보세요.', 0.0, 0),
('서울역 트래블러', '서울특별시 중구 통일로 12 (04527)', '서울', 5, 178000, '서울역과 가까워 지방에서 오시는 분들이나 여행객들에게 편리합니다. 깔끔하고 편안한 잠자리를 제공합니다.', 0.0, 0),
('숲세권 우이동', '서울특별시 강북구 삼양로173길 52 (01028)', '서울', 10, 285000, '북한산 국립공원 아래 위치하여 맑은 공기와 자연을 만끽할 수 있습니다. 대가족이나 단체 모임에 적합합니다.', 0.0, 0),
('낙산공원 야경맛집', '서울특별시 종로구 낙산성곽서1길 17-2 (03087)', '서울', 4, 205000, '서울의 야경 명소인 낙산공원과 가깝습니다. 성곽길을 따라 걸으며 로맨틱한 밤을 보내세요.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('해운대 오션뷰', '부산광역시 해운대구 달맞이길 62번길 28 (48116)', '부산', 6, 250000, '해운대 해수욕장이 한눈에 들어오는 환상적인 오션뷰를 자랑합니다. 파도 소리를 들으며 아침을 맞이하세요.', 0.0, 0),
('광안리 불꽃 스테이', '부산광역시 수영구 광안해변로 219 (48303)', '부산', 5, 230000, '광안대교의 아름다운 야경을 감상할 수 있는 최적의 장소입니다. 불꽃축제 기간에는 최고의 명당이 됩니다.', 0.0, 0),
('남포동 자갈치 하우스', '부산광역시 중구 자갈치해안로 52 (48983)', '부산', 8, 200000, '자갈치시장과 국제시장이 가까워 부산의 맛과 멋을 즐기기 좋습니다. 활기찬 시장의 에너지를 느껴보세요.', 0.0, 0),
('송도 케이블카 앞', '부산광역시 서구 충무대로 94 (49275)', '부산', 4, 180000, '송도해상케이블카와 스카이워크가 바로 앞에 있습니다. 바다 위를 걷는 듯한 짜릿한 경험을 해보세요.', 0.0, 0),
('기장 웨이브온', '부산광역시 기장군 장안읍 해맞이로 286 (46035)', '부산', 7, 280000, '부산 근교의 한적한 바닷가에 위치한 모던한 풀빌라입니다. 프라이빗한 수영장에서 여유로운 시간을 보내세요.', 0.0, 0),
('감천문화마을 알록달록', '부산광역시 사하구 감내2로 203 (49368)', '부산', 3, 150000, '한국의 마추픽추라 불리는 감천문화마을 안에 위치하고 있습니다. 예술적인 감성과 아름다운 풍경을 동시에 즐겨보세요.', 0.0, 0),
('서면 젊음의 거리', '부산광역시 부산진구 중앙대로692번길 39 (47287)', '부산', 6, 190000, '부산 최대 번화가인 서면에 위치하여 교통이 편리하고 놀거리가 많습니다. 친구들과 함께 신나는 밤을 보내세요.', 0.0, 0),
('영도 흰여울마을', '부산광역시 영도구 흰여울길 379 (49019)', '부산', 4, 170000, '절벽 위 해안산책로를 따라 자리잡은 그림 같은 마을의 숙소입니다. 영화 속 주인공이 된 듯한 기분을 느껴보세요.', 0.0, 0),
('태종대 조약돌 하우스', '부산광역시 영도구 전망로 119 (49129)', '부산', 5, 160000, '태종대유원지와 가까워 아름다운 자연 경관을 감상하기 좋습니다. 유람선을 타고 부산의 해안 절경을 즐겨보세요.', 0.0, 0),
('다대포 선셋 비치', '부산광역시 사하구 다대로 692 (49520)', '부산', 6, 210000, '아름다운 일몰로 유명한 다대포 해수욕장 앞에 위치하고 있습니다. 붉게 물드는 하늘을 바라보며 낭만적인 저녁을 보내세요.', 0.0, 0),
('전포 카페거리 감성숙소', '부산광역시 부산진구 전포대로209번길 17-9 (47285)', '부산', 4, 185000, '개성 넘치는 카페와 소품샵이 모여있는 전포 카페거리에 있습니다. 아기자기한 공간에서 특별한 추억을 만들어보세요.', 0.0, 0),
('동래 온천 힐링 스테이', '부산광역시 동래구 금강공원로26번길 30 (47709)', '부산', 5, 205000, '오랜 역사를 지닌 동래온천 인근에 위치하고 있습니다. 따뜻한 온천수로 여행의 피로를 풀어보세요.', 0.0, 0),
('송정 서핑 하우스', '부산광역시 해운대구 송정해변로 62 (48071)', '부산', 8, 240000, '서핑의 성지, 송정해수욕장 바로 앞에 위치한 숙소입니다. 서핑 강습도 받고 바다의 에너지를 만끽하세요.', 0.0, 0),
('이기대 해안산책로', '부산광역시 남구 용호로 266-30 (48576)', '부산', 6, 220000, '이기대 도시자연공원과 가까워 아름다운 해안산책로를 따라 트레킹을 즐길 수 있습니다. 건강한 휴식을 원하는 분들께 추천합니다.', 0.0, 0),
('보수동 책방골목 옆', '부산광역시 중구 책방골목길 8 (48977)', '부산', 3, 140000, '헌책방들이 모여있는 보수동 책방골목의 정취를 느껴보세요. 조용한 분위기에서 독서와 함께하는 휴식을 즐길 수 있습니다.', 0.0, 0),
('해리단길 아지트', '부산광역시 해운대구 우동1로38번길 11 (48089)', '부산', 4, 175000, '옛 해운대역 뒷골목이 아기자기한 가게들로 채워진 해리단길에 있습니다. 숨겨진 맛집과 카페를 탐방하는 재미를 느껴보세요.', 0.0, 0),
('황령산 봉수대 뷰', '부산광역시 부산진구 황령산로 391-30 (47321)', '부산', 7, 260000, '황령산 중턱에 위치하여 부산 시내와 광안대교의 파노라마 야경을 감상할 수 있습니다. 잊지 못할 밤을 선사합니다.', 0.0, 0),
('범어사 템플뷰', '부산광역시 금정구 범어사로 250 (46206)', '부산', 5, 195000, '천년고찰 범어사가 가까워 고즈넉한 산사의 정취를 느낄 수 있습니다. 맑은 공기를 마시며 마음의 평화를 찾아보세요.', 0.0, 0),
('마린시티 요트 스테이', '부산광역시 해운대구 마린시티1로 132 (48120)', '부산', 6, 290000, '고층 빌딩이 숲을 이룬 마린시티에 위치한 고급스러운 숙소입니다. 요트 경기장과 더베이101의 화려한 야경을 즐겨보세요.', 0.0, 0),
('대변항 멸치 하우스', '부산광역시 기장군 기장읍 기장해안로 572 (46083)', '부산', 8, 215000, '봄이면 멸치 축제가 열리는 대변항에 위치하고 있습니다. 신선한 해산물을 맛보고 어촌의 활기를 느껴보세요.', 0.0, 0),
('을숙도 철새 도래지', '부산광역시 사하구 낙동남로1240번길 10 (49430)', '부산', 5, 165000, '낙동강 하구의 아름다운 자연 생태를 관찰할 수 있는 을숙도생태공원과 가깝습니다. 자전거를 타며 여유로운 시간을 보내세요.', 0.0, 0),
('UN기념공원 옆', '부산광역시 남구 유엔평화로 93 (48528)', '부산', 4, 155000, '세계 유일의 UN군 묘지인 UN기념공원 인근에 위치하고 있습니다. 평화로운 분위기 속에서 역사를 되새겨보는 시간을 가져보세요.', 0.0, 0),
('오륙도 스카이워크', '부산광역시 남구 오륙도로 137 (48582)', '부산', 6, 235000, '바다 위를 걷는 듯한 아찔함을 느낄 수 있는 오륙도 스카이워크와 가깝습니다. 시원하게 펼쳐진 바다를 감상하세요.', 0.0, 0),
('국제시장 꽃분이네', '부산광역시 중구 신창동4가 (48946)', '부산', 5, 178000, '영화 국제시장의 배경이 된 곳에 위치하고 있습니다. 시장 구석구석을 누비며 부산의 역사를 느껴보세요.', 0.0, 0),
('부산항대교 뷰포인트', '부산광역시 영도구 해양로 195 (49111)', '부산', 7, 245000, '밤이면 아름다운 조명으로 빛나는 부산항대교를 가장 잘 볼 수 있는 곳입니다. 로맨틱한 야경을 배경으로 특별한 밤을 보내세요.', 0.0, 0),
('시민공원 피크닉', '부산광역시 부산진구 시민공원로 73 (47197)', '부산', 8, 225000, '넓은 잔디밭과 숲이 있는 부산시민공원 바로 옆입니다. 가족과 함께 피크닉을 즐기고 여유로운 시간을 보내기 좋습니다.', 0.0, 0),
('죽성드림성당 근처', '부산광역시 기장군 기장읍 죽성리 134-7 (46085)', '부산', 4, 188000, '드라마 촬영지로 유명한 죽성드림성당과 가까운 바닷가 숙소입니다. 그림 같은 풍경 속에서 인생 사진을 남겨보세요.', 0.0, 0),
('초량 이바구길', '부산광역시 동구 초량상로 49 (48731)', '부산', 5, 162000, '부산의 근대 역사를 품고 있는 이바구길에 위치하고 있습니다. 168계단 모노레일을 타고 부산항의 전경을 감상해보세요.', 0.0, 0),
('광복동 트리축제', '부산광역시 중구 광복로 88 (48959)', '부산', 6, 208000, '겨울이면 화려한 빛으로 물드는 광복로 트리축제의 중심에 있습니다. 크리스마스 분위기를 만끽하며 쇼핑을 즐겨보세요.', 0.0, 0),
('F1963 복합문화공간', '부산광역시 수영구 구락로123번길 20 (48212)', '부산', 4, 192000, '오래된 공장을 개조하여 만든 복합문화공간 F1963 근처에 있습니다. 전시, 공연, 서점, 카페를 한 곳에서 즐겨보세요.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('김광석 다시그리기 길', '대구광역시 중구 동덕로8길 14-6 (41951)', '대구', 4, 160000, '故 김광석을 추억하는 벽화거리 중심에 위치한 감성 숙소입니다. 골목길을 따라 흐르는 노래를 들으며 추억에 잠겨보세요.', 0.0, 0),
('동성로 시티라이프', '대구광역시 중구 동성로2길 50 (41940)', '대구', 6, 180000, '대구의 중심, 동성로에 위치하여 쇼핑과 맛집 탐방에 최적입니다. 젊음의 활기를 느끼며 즐거운 시간을 보내세요.', 0.0, 0),
('앞산 카페거리 힐링', '대구광역시 남구 앞산순환로 503 (42409)', '대구', 5, 170000, '아름다운 카페와 레스토랑이 모여있는 앞산 카페거리에 있습니다. 케이블카를 타고 대구 시내의 멋진 야경을 감상해보세요.', 0.0, 0),
('수성못 레이크뷰', '대구광역시 수성구 용학로 39 (42211)', '대구', 7, 220000, '아름다운 수성못이 내려다보이는 전망 좋은 숙소입니다. 오리배를 타거나 산책을 하며 여유로운 시간을 즐겨보세요.', 0.0, 0),
('팔공산 숲속의 집', '대구광역시 동구 팔공산로 1185 (41007)', '대구', 10, 250000, '팔공산 자연공원 안에 위치하여 맑은 공기와 함께 힐링할 수 있습니다. 등산을 즐기고 갓바위에 올라 소원을 빌어보세요.', 0.0, 0),
('서문시장 야시장 옆', '대구광역시 중구 큰장로26길 45 (41922)', '대구', 8, 190000, '대구 최대의 전통시장인 서문시장과 가깝습니다. 밤이 되면 열리는 야시장에서 다양한 먹거리를 즐겨보세요.', 0.0, 0),
('근대골목 투어 스테이', '대구광역시 중구 경상감영길 99 (41919)', '대구', 4, 150000, '대구 근대골목투어 코스 중심에 위치하고 있습니다. 계산성당, 3.1만세운동길 등 역사의 흔적을 따라 걸어보세요.', 0.0, 0),
('이월드 83타워 뷰', '대구광역시 달서구 두류공원로 200 (42666)', '대구', 6, 210000, '대구의 랜드마크인 83타워와 이월드가 보이는 숙소입니다. 놀이공원에서 신나게 놀고 멋진 야경을 감상하세요.', 0.0, 0),
('대구미술관 옆 예술가', '대구광역시 수성구 미술관로 40 (42250)', '대구', 5, 175000, '대구미술관과 가까워 문화생활을 즐기기 좋습니다. 조용한 환경에서 예술적 영감을 얻으며 휴식을 취해보세요.', 0.0, 0),
('안지랑 곱창골목', '대구광역시 남구 대명로36길 63 (42402)', '대구', 7, 185000, '전국적으로 유명한 안지랑 곱창골목에 위치하고 있습니다. 저렴하고 맛있는 곱창을 마음껏 즐겨보세요.', 0.0, 0),
('동인동 찜갈비 골목', '대구광역시 중구 동덕로36길 9-17 (41942)', '대구', 6, 178000, '매콤한 찜갈비로 유명한 동인동 찜갈비 골목에 있습니다. 화끈한 맛으로 스트레스를 날려버리세요.', 0.0, 0),
('강정보 디아크', '대구광역시 달성군 다사읍 강정본길 57 (42911)', '대구', 8, 230000, '자전거 라이딩과 산책 명소인 강정보 디아크 인근에 위치하고 있습니다. 아름다운 건축물과 낙동강의 풍경을 즐겨보세요.', 0.0, 0),
('비슬산 자연휴양림', '대구광역시 달성군 유가읍 휴양림길 230 (43011)', '대구', 10, 260000, '봄이면 참꽃으로 유명한 비슬산 자연휴양림 안에 있습니다. 맑은 계곡물 소리를 들으며 삼림욕을 즐겨보세요.', 0.0, 0),
('대구 스타디움', '대구광역시 수성구 유니버시아드로 180 (42268)', '대구', 7, 200000, '대구 스타디움과 가까워 스포츠 경기나 공연을 관람하기 편리합니다. 넓은 공원에서 여유롭게 산책을 즐겨보세요.', 0.0, 0),
('평화시장 닭똥집골목', '대구광역시 동구 아양로9길 10 (41191)', '대구', 6, 165000, '대구의 명물, 닭똥집 튀김을 맛볼 수 있는 평화시장에 있습니다. 바삭하고 고소한 맛의 매력에 빠져보세요.', 0.0, 0),
('국채보상운동기념공원', '대구광역시 중구 국채보상로 670 (41945)', '대구', 5, 155000, '대구의 정신을 상징하는 국채보상운동기념공원 옆에 위치하고 있습니다. 도심 속 공원에서 평화로운 휴식을 즐겨보세요.', 0.0, 0),
('옻골마을 한옥체험', '대구광역시 동구 옻골로 195-5 (41061)', '대구', 8, 240000, '오래된 고택들이 잘 보존된 옻골마을에서 특별한 한옥 체험을 해보세요. 전통 문화를 느끼며 고즈넉한 시간을 보낼 수 있습니다.', 0.0, 0),
('대구삼성라이온즈파크', '대구광역시 수성구 야구전설로 1 (42261)', '대구', 6, 215000, '야구 팬들을 위한 최적의 숙소, 라팍 바로 근처에 위치하고 있습니다. 야구 경기를 관람하며 뜨거운 열기를 느껴보세요.', 0.0, 0),
('마비정 벽화마을', '대구광역시 달성군 화원읍 마비정길 7 (42935)', '대구', 4, 145000, '60-70년대 농촌의 모습을 정겨운 벽화로 재현한 마비정 벽화마을에 있습니다. 옛 추억을 떠올리며 사진을 찍어보세요.', 0.0, 0),
('봉산문화거리', '대구광역시 중구 봉산문화길 70 (41958)', '대구', 3, 130000, '화랑과 소극장, 고미술품점이 모여있는 봉산문화거리에 있습니다. 예술의 향기를 느끼며 여유로운 시간을 보내세요.', 0.0, 0),
('앞산 빨래터공원', '대구광역시 남구 명덕로50길 51 (42416)', '대구', 5, 168000, '앞산 자락에 위치한 조용한 공원 옆 숙소입니다. 맑은 공기를 마시며 산책하기 좋은 곳입니다.', 0.0, 0),
('대구콘서트하우스', '대구광역시 중구 태평로 141 (41927)', '대구', 4, 172000, '클래식 공연을 즐길 수 있는 대구콘서트하우스와 가깝습니다. 품격 있는 문화생활과 함께하는 여행을 계획해보세요.', 0.0, 0),
('달성공원 동물원', '대구광역시 중구 달성공원로 35 (41931)', '대구', 7, 188000, '오랜 역사를 지닌 달성공원과 동물원 바로 옆입니다. 아이들과 함께 즐거운 시간을 보내기에 좋습니다.', 0.0, 0),
('불로동 고분군', '대구광역시 동구 불로동 335 (41021)', '대구', 5, 158000, '삼국시대의 고분들이 모여있는 역사적인 장소입니다. 고분 사이를 산책하며 신비로운 분위기를 느껴보세요.', 0.0, 0),
('경상감영공원', '대구광역시 중구 경상감영길 99 (41919)', '대구', 4, 148000, '조선시대 경상감영이 있던 자리에 조성된 공원입니다. 도심 속에서 역사의 숨결을 느끼며 휴식을 취할 수 있습니다.', 0.0, 0),
('향촌문화관', '대구광역시 중구 중앙대로 449 (41932)', '대구', 3, 135000, '1950년대 대구의 모습을 재현해 놓은 향촌문화관 근처입니다. 부모님과 함께 추억 여행을 떠나보세요.', 0.0, 0),
('네이처파크', '대구광역시 달성군 가창면 가창로 891 (42923)', '대구', 8, 235000, '동물원과 식물원이 어우러진 자연 교감 테마파크 네이처파크와 가깝습니다. 아이들에게 특별한 경험을 선물하세요.', 0.0, 0),
('대구 근대골목 단팥빵', '대구광역시 중구 남성로 54 (41935)', '대구', 4, 152000, '대구 명물인 근대골목 단팥빵 본점 근처에 위치하고 있습니다. 갓 나온 따끈한 빵을 맛보며 여행을 시작해보세요.', 0.0, 0),
('스파밸리 워터파크', '대구광역시 달성군 가창면 가창로 891 (42923)', '대구', 10, 270000, '사계절 내내 물놀이를 즐길 수 있는 스파밸리 워터파크 인근입니다. 가족, 친구들과 함께 신나는 하루를 보내세요.', 0.0, 0),
('교동시장 귀금속거리', '대구광역시 중구 교동길 43 (41913)', '대구', 4, 162000, '다양한 귀금속 상점이 모여있는 교동시장에 위치하고 있습니다. 반짝이는 보석들을 구경하는 재미를 느껴보세요.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('월미도 바다열차', '인천광역시 중구 월미문화로 81 (22303)', '인천', 6, 180000, '월미도 테마파크와 바다열차를 즐길 수 있는 최적의 위치입니다. 서해의 아름다운 낙조를 감상하며 낭만적인 밤을 보내세요.', 0.0, 0),
('송도 센트럴파크', '인천광역시 연수구 컨벤시아대로 160 (21998)', '인천', 7, 240000, '미래도시 송도의 중심, 센트럴파크가 내려다보이는 모던한 숙소입니다. 수상택시를 타며 이국적인 풍경을 즐겨보세요.', 0.0, 0),
('차이나타운 붉은 등', '인천광역시 중구 차이나타운로 41 (22312)', '인천', 5, 160000, '이국적인 분위기가 물씬 풍기는 차이나타운 중심에 있습니다. 맛있는 짜장면과 함께 중국 문화를 체험해보세요.', 0.0, 0),
('강화도 갯벌 체험', '인천광역시 강화군 화도면 해안남로 1481 (23062)', '인천', 8, 200000, '세계 5대 갯벌 중 하나인 강화도 갯벌에서 즐거운 체험을 할 수 있습니다. 아이들과 함께 조개를 캐며 자연을 느껴보세요.', 0.0, 0),
('을왕리 해수욕장', '인천광역시 중구 용유서로302번길 16-15 (22383)', '인천', 10, 260000, '수도권에서 가장 사랑받는 을왕리 해수욕장 바로 앞에 위치하고 있습니다. 넓은 백사장에서 해수욕을 즐기고 조개구이를 맛보세요.', 0.0, 0),
('소래포구 어시장', '인천광역시 남동구 소래역로 12 (21666)', '인천', 7, 190000, '갓 잡은 신선한 해산물을 맛볼 수 있는 소래포구 종합어시장과 가깝습니다. 활기 넘치는 어시장의 풍경을 즐겨보세요.', 0.0, 0),
('동화마을 무지개집', '인천광역시 중구 동화마을길 38 (22313)', '인천', 4, 150000, '세계 명작 동화를 테마로 꾸며진 송월동 동화마을에 있습니다. 동심으로 돌아가 예쁜 사진을 많이 남겨보세요.', 0.0, 0),
('영종도 씨사이드파크', '인천광역시 중구 하늘달빛로 2 (22379)', '인천', 8, 220000, '레일바이크와 캠핑을 즐길 수 있는 영종도 씨사이드파크 인근입니다. 바다를 보며 자전거를 타고 시원한 바람을 느껴보세요.', 0.0, 0),
('전등사 템플뷰', '인천광역시 강화군 길상면 전등사로 37-41 (23050)', '인천', 5, 170000, '현존하는 가장 오래된 사찰인 전등사가 가까워 고즈넉한 산사의 정취를 느낄 수 있습니다. 템플스테이를 통해 마음의 평화를 찾아보세요.', 0.0, 0),
('신포국제시장 닭강정', '인천광역시 중구 우현로49번길 3 (22315)', '인천', 6, 165000, '수요미식회에도 나온 유명한 신포 닭강정을 맛볼 수 있는 신포국제시장에 있습니다. 바삭하고 매콤달콤한 맛을 즐겨보세요.', 0.0, 0),
('석모도 미네랄 온천', '인천광역시 강화군 삼산면 삼산남로 865-17 (23007)', '인천', 6, 210000, '바다를 보며 즐기는 노천 온천으로 유명한 석모도 미네랄 온천 근처입니다. 따뜻한 온천물에 몸을 담그고 피로를 풀어보세요.', 0.0, 0),
('개항장 거리 근대건축', '인천광역시 중구 제물량로218번길 3-1 (22314)', '인천', 4, 155000, '개항기 시대의 건축물들이 잘 보존된 개항장 거리에 있습니다. 역사의 흔적을 따라 걸으며 시간 여행을 떠나보세요.', 0.0, 0),
('인천대공원 숲속', '인천광역시 남동구 무네미로 236 (21553)', '인천', 9, 230000, '넓은 호수와 울창한 숲이 있는 인천대공원 옆입니다. 사계절 아름다운 자연 속에서 가족과 함께 즐거운 시간을 보내세요.', 0.0, 0),
('파라다이스시티', '인천광역시 중구 영종해안남로321번길 186 (22382)', '인천', 7, 290000, '호텔, 카지노, 스파, 클럽 등이 모여있는 복합 리조트 파라다이스시티 인근입니다. 화려하고 즐거운 호캉스를 즐겨보세요.', 0.0, 0),
('자유공원 맥아더 동상', '인천광역시 중구 자유공원남로 25 (22315)', '인천', 5, 140000, '우리나라 최초의 서구식 공원인 자유공원에 있습니다. 맥아더 장군 동상 앞에서 인천상륙작전의 역사를 되새겨보세요.', 0.0, 0),
('부평 지하상가 쇼핑', '인천광역시 부평구 광장로 16 (21389)', '인천', 6, 175000, '단일 면적으로 세계 최대 규모를 자랑하는 부평 지하상가와 가깝습니다. 저렴하고 다양한 물건들을 쇼핑하는 재미를 느껴보세요.', 0.0, 0),
('아라뱃길 카약 체험', '인천광역시 서구 정서진1로 41 (22835)', '인천', 8, 225000, '경인 아라뱃길에서 카약, 요트 등 다양한 수상 레저를 즐길 수 있습니다. 시원한 강바람을 맞으며 스트레스를 날려보세요.', 0.0, 0),
('강화역사박물관', '인천광역시 강화군 하점면 강화대로 994-19 (23011)', '인천', 5, 168000, '선사시대부터 근현대까지 강화도의 역사를 한눈에 볼 수 있는 강화역사박물관 옆입니다. 아이들 교육에도 좋은 여행지입니다.', 0.0, 0),
('무의도 하나개해수욕장', '인천광역시 중구 하나개로 151-2 (22386)', '인천', 10, 250000, '드라마 촬영지로 유명한 하나개해수욕장에 위치하고 있습니다. 갯벌 체험과 짚라인 등 다양한 즐길 거리가 있습니다.', 0.0, 0),
('인천상륙작전기념관', '인천광역시 연수구 청량로 138 (21925)', '인천', 6, 185000, '6.25 전쟁의 역사를 바꾼 인천상륙작전을 기념하는 곳입니다. 자유와 평화의 소중함을 느낄 수 있는 의미 있는 장소입니다.', 0.0, 0),
('청라호수공원', '인천광역시 서구 크리스탈로 131 (22765)', '인천', 7, 235000, '음악분수와 레이저쇼가 아름다운 청라호수공원 근처입니다. 밤이면 더욱 화려해지는 공원에서 낭만적인 시간을 보내세요.', 0.0, 0),
('교동도 대룡시장', '인천광역시 강화군 교동면 대룡안길54번길 24 (23001)', '인천', 4, 145000, '60-70년대 시장의 모습을 그대로 간직하고 있는 교동 대룡시장에 있습니다. 옛날 교복을 입고 추억의 사진을 찍어보세요.', 0.0, 0),
('문학경기장', '인천광역시 미추홀구 매소홀로 618 (22234)', '인천', 8, 205000, 'SSG 랜더스의 홈구장인 문학경기장과 가깝습니다. 야구 경기를 관람하며 신나는 응원을 즐겨보세요.', 0.0, 0),
('수도국산달동네박물관', '인천광역시 동구 솔빛로 51 (22519)', '인천', 5, 158000, '1960~70년대 인천의 달동네 생활 모습을 재현한 박물관입니다. 부모님과 함께 옛 시절의 추억을 이야기해보세요.', 0.0, 0),
('강화평화전망대', '인천광역시 강화군 양사면 전망대로 797 (23010)', '인천', 6, 195000, '북한 땅을 가장 가까이서 볼 수 있는 강화평화전망대입니다. 망원경을 통해 북한 주민들의 생활 모습을 살펴보세요.', 0.0, 0),
('선재도 목섬', '인천광역시 옹진군 영흥면 선재로 34 (22384)', '인천', 4, 172000, '하루 두 번 썰물 때 바닷길이 열리는 신비로운 섬, 목섬과 가깝습니다. 모세의 기적을 체험하며 아름다운 풍경을 감상하세요.', 0.0, 0),
('BMW 드라이빙 센터', '인천광역시 중구 운서동 2801-2 (22379)', '인천', 5, 280000, 'BMW의 다양한 차종을 직접 운전해볼 수 있는 특별한 체험 공간입니다. 스릴 넘치는 드라이빙을 즐겨보세요.', 0.0, 0),
('인천아트플랫폼', '인천광역시 중구 제물량로218번길 3 (22314)', '인천', 3, 130000, '개항기 창고 건물을 리모델링한 복합문화예술 공간입니다. 다양한 전시와 공연을 무료로 즐길 수 있습니다.', 0.0, 0),
('연안부두 밴댕이회무침', '인천광역시 중구 연안부두로 33 (22343)', '인천', 7, 188000, '인천의 명물, 밴댕이회무침을 맛볼 수 있는 연안부두에 있습니다. 새콤달콤한 맛이 일품입니다.', 0.0, 0),
('송도 트리플스트리트', '인천광역시 연수구 송도과학로16번길 33-3 (21984)', '인천', 6, 218000, '쇼핑, 맛집, 영화관이 한곳에 모여있는 복합 쇼핑몰입니다. 하루 종일 시간 가는 줄 모르고 즐길 수 있습니다.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('양림동 펭귄마을', '광주광역시 남구 천변좌로446번길 7 (61631)', '광주', 4, 150000, '정크 아트로 꾸며진 독특한 펭귄마을에 위치하고 있습니다. 아기자기한 골목길을 산책하며 예술적 감성을 느껴보세요.', 0.0, 0),
('충장로 페스티벌', '광주광역시 동구 충장로 70 (61482)', '광주', 6, 170000, '광주의 명동이라 불리는 충장로에 위치하여 쇼핑과 문화를 즐기기 좋습니다. 매년 가을 열리는 추억의 충장축제를 즐겨보세요.', 0.0, 0),
('무등산 국립공원', '광주광역시 동구 증심사길 143 (61493)', '광주', 10, 230000, '광주의 상징인 무등산 국립공원 자락에 위치하고 있습니다. 맑은 공기를 마시며 등산을 즐기고 주상절리의 비경을 감상하세요.', 0.0, 0),
('1913송정역시장', '광주광역시 광산구 송정로8번길 13 (62430)', '광주', 5, 160000, '100년의 역사를 간직한 전통시장을 현대적으로 재해석한 1913송정역시장에 있습니다. 다양한 길거리 음식을 맛보는 재미가 쏠쏠합니다.', 0.0, 0),
('국립아시아문화전당', '광주광역시 동구 문화전당로 38 (61485)', '광주', 7, 200000, '아시아 문화의 교류와 창작이 이루어지는 복합문화공간입니다. 다양한 전시, 공연, 체험 프로그램을 즐겨보세요.', 0.0, 0),
('대인예술시장 별장', '광주광역시 동구 제봉로194번길 7-1 (61474)', '광주', 4, 140000, '매주 토요일 밤, 예술가와 상인이 함께 만드는 특별한 야시장이 열립니다. 독특한 예술 작품과 맛있는 음식을 함께 즐겨보세요.', 0.0, 0),
('5.18 민주광장', '광주광역시 동구 금남로 245 (61485)', '광주', 6, 180000, '5.18 민주화운동의 중심지였던 역사적인 장소입니다. 민주주의의 소중함을 되새기며 의미 있는 시간을 가져보세요.', 0.0, 0),
('광주기아챔피언스필드', '광주광역시 북구 서림로 10 (61264)', '광주', 8, 210000, 'KIA 타이거즈의 홈구장 바로 옆에 위치하고 있습니다. 야구 팬이라면 놓칠 수 없는 최고의 숙소입니다.', 0.0, 0),
('사직공원 전망타워', '광주광역시 남구 사직길 49 (61635)', '광주', 5, 155000, '사직공원 전망타워에 오르면 광주 시내를 한눈에 내려다볼 수 있습니다. 아름다운 야경을 감상하며 로맨틱한 시간을 보내세요.', 0.0, 0),
('지산유원지 모노레일', '광주광역시 동구 지호로164번길 14-10 (61491)', '광주', 7, 190000, '리프트를 타고 올라가 모노레일을 즐길 수 있는 지산유원지 인근입니다. 짜릿한 스릴과 함께 멋진 풍경을 감상하세요.', 0.0, 0),
('광주호 호수생태원', '광주광역시 북구 충효동 442-5 (61153)', '광주', 8, 220000, '아름다운 호수와 다양한 수생식물을 관찰할 수 있는 생태공원입니다. 가족과 함께 자연 속에서 여유로운 시간을 보내세요.', 0.0, 0),
('동명동 카페거리', '광주광역시 동구 동명로 26 (61477)', '광주', 4, 165000, '개성 넘치는 카페와 맛집이 모여있는 동명동 카페거리에 있습니다. 예쁜 카페에서 달콤한 디저트와 함께 휴식을 취하세요.', 0.0, 0),
('광주패밀리랜드', '광주광역시 북구 우치로 677 (61163)', '광주', 10, 240000, '놀이공원, 동물원, 눈썰매장 등이 모여있는 종합 위락공원입니다. 아이들과 함께 하루 종일 신나게 놀 수 있습니다.', 0.0, 0),
('국립광주과학관', '광주광역시 북구 첨단과기로 235 (61005)', '광주', 7, 195000, '아이들의 상상력과 창의력을 키워주는 국립광주과학관과 가깝습니다. 다양한 과학 체험을 통해 즐겁게 배워보세요.', 0.0, 0),
('전남대학교 캠퍼스', '광주광역시 북구 용봉로 77 (61186)', '광주', 5, 145000, '아름다운 캠퍼스로 유명한 전남대학교 근처에 위치하고 있습니다. 젊음의 열기를 느끼며 캠퍼스를 산책해보세요.', 0.0, 0),
('광주비엔날레 전시관', '광주광역시 북구 비엔날레로 111 (61104)', '광주', 6, 185000, '2년마다 열리는 세계적인 현대미술 축제, 광주비엔날레 전시관 인근입니다. 수준 높은 예술 작품을 감상하며 영감을 얻어보세요.', 0.0, 0),
('의재미술관', '광주광역시 동구 증심사길 155 (61493)', '광주', 3, 130000, '무등산 자락에 자리 잡은 아름다운 미술관입니다. 한국 남종화의 대가, 의재 허백련의 작품을 감상하며 힐링하세요.', 0.0, 0),
('월봉서원', '광주광역시 광산구 광곡길 133 (62413)', '광주', 8, 215000, '조선시대 유학자 고봉 기대승을 모신 서원입니다. 고즈넉한 분위기 속에서 선비 문화를 체험해보세요.', 0.0, 0),
('광주극장', '광주광역시 동구 충장로46번길 10 (61482)', '광주', 4, 135000, '1935년에 개관한 현존하는 가장 오래된 단관극장입니다. 예술영화와 독립영화를 감상하며 특별한 추억을 만들어보세요.', 0.0, 0),
('푸른길 공원', '광주광역시 동구 계림동 561-1 (61470)', '광주', 6, 175000, '폐선된 철길을 활용하여 만든 도심 속 공원입니다. 자전거를 타거나 산책을 하며 도심 속 여유를 즐겨보세요.', 0.0, 0),
('중외공원 문화벨트', '광주광역시 북구 하서로 52 (61104)', '광주', 7, 205000, '시립미술관, 시립민속박물관, 비엔날레 전시관 등이 모여있는 문화예술의 중심지입니다. 다양한 문화생활을 한곳에서 즐겨보세요.', 0.0, 0),
('상무지구 비즈니스', '광주광역시 서구 상무중앙로 7 (61948)', '광주', 5, 188000, '광주의 신도심, 상무지구에 위치하여 비즈니스 출장객에게 편리합니다. 깔끔하고 현대적인 시설을 갖추고 있습니다.', 0.0, 0),
('유스퀘어 문화관', '광주광역시 서구 무진대로 904 (61622)', '광주', 6, 178000, '광주 종합버스터미널과 신세계백화점이 결합된 복합문화공간입니다. 교통, 쇼핑, 문화를 한 번에 해결할 수 있습니다.', 0.0, 0),
('황룡강 생태공원', '광주광역시 광산구 선암동 670 (62401)', '광주', 8, 225000, '가을이면 노란 코스모스가 만발하는 아름다운 생태공원입니다. 자전거길이 잘 조성되어 있어 라이딩을 즐기기 좋습니다.', 0.0, 0),
('전평제 수변공원', '광주광역시 광산구 수완동 1533 (62238)', '광주', 7, 198000, '도심 속에 위치한 아름다운 호수 공원입니다. 저녁에는 음악분수쇼가 펼쳐져 낭만적인 분위기를 더합니다.', 0.0, 0),
('김대중컨벤션센터', '광주광역시 서구 상무누리로 30 (61958)', '광주', 5, 192000, '다양한 전시, 컨벤션, 공연이 열리는 김대중컨벤션센터와 가깝습니다. 행사 참석 및 관람에 편리한 위치입니다.', 0.0, 0),
('포충사', '광주광역시 남구 포충로 767 (61711)', '광주', 6, 162000, '임진왜란 때 활약한 고경명 장군과 의병들을 모신 사당입니다. 호국 정신을 기리며 경건한 시간을 가져보세요.', 0.0, 0),
('광주향교', '광주광역시 남구 구동 1-1 (61629)', '광주', 4, 142000, '조선시대 지방 교육기관이었던 광주향교입니다. 고즈넉한 분위기 속에서 전통 건축의 아름다움을 느껴보세요.', 0.0, 0),
('오리요리 거리', '광주광역시 북구 유동 100-1 (61254)', '광주', 8, 208000, '광주의 대표 음식인 오리탕을 맛볼 수 있는 거리에 있습니다. 진하고 얼큰한 국물 맛이 일품입니다.', 0.0, 0),
('사직공원 통기타 거리', '광주광역시 남구 사동 119-2 (61635)', '광주', 5, 158000, '7080 감성이 살아있는 통기타 라이브 카페가 모여있는 거리입니다. 감미로운 음악과 함께 추억에 젖어보세요.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('성심당 빵집 옆', '대전광역시 중구 대종로480번길 15 (34923)', '대전', 5, 160000, '대전의 명물, 성심당 본점 바로 옆에 위치하고 있습니다. 갓 나온 따끈한 튀김소보로를 맛보며 여행을 시작하세요.', 0.0, 0),
('대청호 오백리길', '대전광역시 동구 회남로 251 (34501)', '대전', 8, 220000, '아름다운 대청호를 따라 걷는 오백리길 인근에 위치한 힐링 숙소입니다. 호수를 바라보며 마음의 여유를 찾아보세요.', 0.0, 0),
('유성온천 족욕체험', '대전광역시 유성구 온천로 81 (34186)', '대전', 6, 180000, '따뜻한 온천수에 발을 담그고 여행의 피로를 풀 수 있는 유성온천 족욕체험장과 가깝습니다. 피부미용에도 효과가 좋습니다.', 0.0, 0),
('국립중앙과학관', '대전광역시 유성구 대덕대로 481 (34143)', '대전', 7, 200000, '우리나라 과학기술의 발전사를 한눈에 볼 수 있는 국립중앙과학관 인근입니다. 아이들과 함께 신기한 과학의 세계를 탐험해보세요.', 0.0, 0),
('한밭수목원', '대전광역시 서구 둔산대로 169 (35201)', '대전', 8, 210000, '도심 속에 위치한 국내 최대 규모의 인공 수목원입니다. 사계절 아름다운 꽃과 나무를 보며 산책을 즐겨보세요.', 0.0, 0),
('엑스포과학공원 한빛탑', '대전광역시 유성구 대덕대로 480 (34126)', '대전', 6, 190000, '1993년 대전 엑스포의 상징인 한빛탑이 있는 엑스포과학공원에 있습니다. 음악분수쇼와 함께 아름다운 야경을 감상하세요.', 0.0, 0),
('오월드 동물원', '대전광역시 중구 사정공원로 70 (35073)', '대전', 10, 250000, '동물원, 플라워랜드, 놀이시설이 어우러진 종합 테마파크입니다. 가족, 연인과 함께 즐거운 하루를 보내기에 완벽한 곳입니다.', 0.0, 0),
('으능정이 문화의 거리', '대전광역시 중구 중앙로170번길 28 (34923)', '대전', 5, 150000, '대전의 중심 번화가로, 스카이로드가 설치되어 밤이면 화려한 영상쇼가 펼쳐집니다. 쇼핑과 맛집 탐방을 즐겨보세요.', 0.0, 0),
('장태산자연휴양림', '대전광역시 서구 장안로 461 (35401)', '대전', 9, 230000, '메타세쿼이아 숲이 울창한 아름다운 자연휴양림입니다. 숲속의 집에서 하룻밤 묵으며 삼림욕을 즐겨보세요.', 0.0, 0),
('계족산 황톳길', '대전광역시 대덕구 산디로 79-70 (34351)', '대전', 7, 170000, '맨발로 부드러운 황톳길을 걸으며 건강을 챙길 수 있는 특별한 장소입니다. 숲속 음악회도 열려 즐거움을 더합니다.', 0.0, 0),
('대전시립미술관', '대전광역시 서구 둔산대로155번길 8 (35204)', '대전', 4, 140000, '다양한 현대미술 작품을 감상할 수 있는 대전시립미술관 옆입니다. 예술을 통해 삶의 여유를 느껴보세요.', 0.0, 0),
('뿌리공원', '대전광역시 중구 뿌리공원로 79 (35083)', '대전', 8, 195000, '자신의 뿌리를 찾아볼 수 있는 세계 유일의 "효" 테마 공원입니다. 가족과 함께 방문하여 조상의 의미를 되새겨보세요.', 0.0, 0),
('카이스트 캠퍼스', '대전광역시 유성구 대학로 291 (34141)', '대전', 5, 165000, '우리나라 최고의 과학기술 인재들이 모이는 카이스트 캠퍼스를 거닐어보세요. 독특한 건물들과 자유로운 분위기를 느낄 수 있습니다.', 0.0, 0),
('대전월드컵경기장', '대전광역시 유성구 월드컵대로 32 (34149)', '대전', 8, 215000, '대전 시티즌의 홈구장으로, 축구 경기가 열리는 날이면 뜨거운 함성으로 가득 찹니다. 축구 팬들을 위한 최적의 숙소입니다.', 0.0, 0),
('이응노미술관', '대전광역시 서구 둔산대로155번길 8 (35204)', '대전', 3, 130000, '프랑스에서 활동한 고암 이응노 화백의 작품 세계를 만날 수 있는 곳입니다. 독창적인 건축물과 예술 작품이 조화를 이룹니다.', 0.0, 0),
('대전스카이로드', '대전광역시 중구 중앙로 164 (34923)', '대전', 6, 175000, '으능정이 거리에 설치된 거대한 LED 스크린으로, 밤마다 화려한 영상과 음악이 펼쳐집니다. 환상적인 미디어아트를 감상해보세요.', 0.0, 0),
('화폐박물관', '대전광역시 유성구 과학로 80-67 (34132)', '대전', 5, 145000, '우리나라 화폐의 역사를 한눈에 볼 수 있는 화폐박물관입니다. 아이들에게 경제 관념을 심어줄 수 있는 유익한 장소입니다.', 0.0, 0),
('우암사적공원', '대전광역시 동구 충정로 53 (34612)', '대전', 7, 185000, '조선 후기 유학자 우암 송시열이 머물던 곳에 조성된 공원입니다. 고즈넉한 한옥 건물과 아름다운 정원을 산책해보세요.', 0.0, 0),
('둔산동 타임월드', '대전광역시 서구 대덕대로 211 (35229)', '대전', 6, 205000, '갤러리아백화점 타임월드점이 위치한 대전의 쇼핑 중심지입니다. 명품 쇼핑과 함께 다양한 맛집을 즐길 수 있습니다.', 0.0, 0),
('대전 아쿠아리움', '대전광역시 중구 대사동 198-14 (34962)', '대전', 8, 225000, '다양한 해양 생물을 만날 수 있는 대전 아쿠아리움입니다. 악어쇼 등 특별한 공연도 관람할 수 있어 아이들이 좋아합니다.', 0.0, 0),
('옛 충남도청사', '대전광역시 중구 중앙로 101 (34864)', '대전', 4, 135000, '근대 건축물로 등록문화재로 지정된 옛 충남도청사 건물입니다. 현재는 대전근현대사전시관으로 사용되고 있습니다.', 0.0, 0),
('유림공원', '대전광역시 유성구 어은로 27 (34139)', '대전', 6, 178000, '가을이면 국화축제가 열리는 아름다운 공원입니다. 갑천변을 따라 자전거를 타거나 산책을 즐기기 좋습니다.', 0.0, 0),
('대전예술의전당', '대전광역시 서구 둔산대로135번길 20 (35204)', '대전', 5, 188000, '오페라, 뮤지컬, 콘서트 등 다양한 장르의 공연이 열리는 대전의 대표적인 문화예술 공간입니다. 품격 있는 공연을 즐겨보세요.', 0.0, 0),
('로하스 해피로드', '대전광역시 대덕구 대청로 424 (34367)', '대전', 7, 208000, '대청호반을 따라 조성된 아름다운 산책길입니다. 금강로하스대청공원과 이어져 있어 여유롭게 걷기 좋습니다.', 0.0, 0),
('대전시민천문대', '대전광역시 유성구 과학로 213-48 (34128)', '대전', 5, 158000, '별과 우주에 대한 꿈을 키울 수 있는 시민천문대입니다. 천체망원경으로 신비로운 밤하늘을 관측해보세요.', 0.0, 0),
('한밭교육박물관', '대전광역시 동구 우암로 96 (34616)', '대전', 4, 125000, '옛날 교실과 교과서 등 교육 관련 자료들이 전시되어 있습니다. 부모님과 함께 학창 시절의 추억을 떠올려보세요.', 0.0, 0),
('만인산자연휴양림', '대전광역시 동구 하소동 산48-1 (34682)', '대전', 10, 245000, '울창한 숲과 맑은 계곡이 있는 자연휴양림입니다. 숲속에서 바베큐를 즐기고 상쾌한 공기를 마시며 힐링하세요.', 0.0, 0),
('지질박물관', '대전광역시 유성구 과학로 124 (34132)', '대전', 6, 152000, '공룡 화석과 다양한 암석, 광물을 볼 수 있는 지질박물관입니다. 지구의 역사를 배우는 신기하고 재미있는 공간입니다.', 0.0, 0),
('궁동 로데오거리', '대전광역시 유성구 궁동 427-1 (34134)', '대전', 5, 168000, '충남대학교 앞에 위치한 젊음의 거리입니다. 저렴하고 맛있는 음식점과 개성 넘치는 카페들이 많이 있습니다.', 0.0, 0),
('대전중앙시장', '대전광역시 동구 대전로 783 (34624)', '대전', 7, 182000, '대전에서 가장 큰 규모의 전통시장입니다. 활기 넘치는 시장을 구경하고 맛있는 순대와 칼국수를 맛보세요.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('대왕암공원 출렁다리', '울산광역시 동구 등대로 95 (44027)', '울산', 6, 190000, '기암괴석과 푸른 바다가 어우러진 대왕암공원에 있습니다. 아찔한 출렁다리를 건너며 스릴과 아름다운 풍경을 동시에 즐겨보세요.', 0.0, 0),
('태화강 국가정원', '울산광역시 중구 태화강국가정원길 154 (44429)', '울산', 7, 210000, '십리대숲으로 유명한 태화강 국가정원 인근에 위치하고 있습니다. 은하수길의 환상적인 야경을 감상하며 힐링하세요.', 0.0, 0),
('간절곶 해돋이', '울산광역시 울주군 서생면 간절곶1길 39-2 (45010)', '울산', 8, 230000, '한반도에서 해가 가장 먼저 뜨는 곳으로 유명한 간절곶에 있습니다. 소망우체통에 편지를 보내고 새해 첫 해를 맞이해보세요.', 0.0, 0),
('장생포 고래문화마을', '울산광역시 남구 장생포고래로 244 (44780)', '울산', 5, 170000, '과거 고래잡이 전진기지였던 장생포의 옛 모습을 재현한 마을입니다. 고래박물관과 생태체험관에서 고래에 대해 배워보세요.', 0.0, 0),
('가지산 철쭉 터널', '울산광역시 울주군 상북면 등억알프스리 517-3 (44951)', '울산', 10, 250000, '영남알프스의 최고봉인 가지산 자락에 위치하고 있습니다. 봄이면 아름다운 철쭉이 터널을 이루는 장관을 볼 수 있습니다.', 0.0, 0),
('일산해수욕장', '울산광역시 동구 해수욕장10길 18 (44028)', '울산', 8, 200000, '울산의 대표적인 해수욕장으로, 모래가 곱고 수심이 얕아 가족 단위 피서객에게 인기가 많습니다. 해양 레포츠를 즐겨보세요.', 0.0, 0),
('울산대공원', '울산광역시 남구 대공원로 94 (44662)', '울산', 9, 220000, '동물원, 장미원, 나비생태관 등 다양한 시설을 갖춘 도심 속 휴식 공간입니다. 넓은 공원에서 자전거를 타며 여유를 만끽하세요.', 0.0, 0),
('반구대 암각화', '울산광역시 울주군 언양읍 대곡리 991 (44913)', '울산', 4, 150000, '선사시대 사람들이 바위에 새겨놓은 그림으로, 국보 제285호로 지정되어 있습니다. 인류의 소중한 문화유산을 직접 확인해보세요.', 0.0, 0),
('신불산 억새평원', '울산광역시 울주군 삼남면 가천리 931-1 (44950)', '울산', 7, 180000, '가을이면 은빛 억새가 장관을 이루는 신불산 억새평원과 가깝습니다. 등산을 하며 아름다운 자연의 풍경에 취해보세요.', 0.0, 0),
('주전 몽돌해변', '울산광역시 동구 주전해안길 80 (44041)', '울산', 6, 160000, '파도에 씻겨 동글동글해진 몽돌이 해변을 가득 채우고 있습니다. 파도가 몽돌에 부딪히는 소리를 들으며 힐링하세요.', 0.0, 0),
('울산대교 전망대', '울산광역시 동구 봉수로 155-1 (44016)', '울산', 5, 175000, '울산대교와 울산항, 산업단지의 야경을 한눈에 감상할 수 있는 곳입니다. 화려하게 빛나는 공단의 불빛이 장관을 이룹니다.', 0.0, 0),
('외고산 옹기마을', '울산광역시 울주군 온양읍 외고산3길 36 (45013)', '울산', 8, 195000, '전국 최대 규모의 민속 옹기마을로, 옹기 장인들의 숨결을 느낄 수 있습니다. 옹기 만들기 체험을 통해 특별한 추억을 만들어보세요.', 0.0, 0),
('석남사 비구니 사찰', '울산광역시 울주군 상북면 석남로 557 (44952)', '울산', 6, 165000, '가지산 자락에 위치한 아름다운 비구니 스님들의 사찰입니다. 고즈넉한 산사에서 마음의 평온을 찾아보세요.', 0.0, 0),
('태화루', '울산광역시 중구 태화로 300 (44446)', '울산', 7, 205000, '태화강변에 복원된 누각으로, 울산 시내와 태화강의 풍경을 조망할 수 있습니다. 시원한 강바람을 맞으며 풍류를 즐겨보세요.', 0.0, 0),
('파래소폭포', '울산광역시 울주군 상북면 이천리 917-2 (44952)', '울산', 5, 155000, '신불산 자연휴양림 안에 위치한 시원한 폭포입니다. 여름철 더위를 식히기에 더없이 좋은 장소입니다.', 0.0, 0),
('울산박물관', '울산광역시 남구 두왕로 277 (44672)', '울산', 6, 178000, '울산의 역사와 산업 발전사를 한눈에 볼 수 있는 곳입니다. 아이들과 함께 울산에 대해 배워보는 유익한 시간을 가져보세요.', 0.0, 0),
('성남동 젊음의거리', '울산광역시 중구 젊음의거리 40 (44538)', '울산', 7, 188000, '울산의 중심 번화가로, 다양한 상점과 맛집, 영화관이 밀집해 있습니다. 젊음의 활기를 느끼며 즐거운 시간을 보내세요.', 0.0, 0),
('자수정 동굴나라', '울산광역시 울주군 상북면 자수정로 212 (44951)', '울산', 9, 240000, '자수정 광산을 개발하여 만든 테마파크입니다. 동굴 보트를 타고 신비로운 지하 세계를 탐험해보세요.', 0.0, 0),
('현대자동차 울산공장', '울산광역시 북구 염포로 700 (44203)', '울산', 5, 162000, '세계적인 자동차 기업 현대자동차의 생산 과정을 견학할 수 있습니다. 미리 예약해야 하니 참고하세요.', 0.0, 0),
('울기등대', '울산광역시 동구 등대로 155 (44027)', '울산', 4, 145000, '대왕암공원 안에 위치한 아름다운 등대입니다. 100년이 넘는 역사를 지니고 있으며, 동해의 길잡이 역할을 하고 있습니다.', 0.0, 0),
('진하해수욕장', '울산광역시 울주군 서생면 진하해변길 58 (45001)', '울산', 8, 215000, '넓은 백사장과 맑은 바닷물을 자랑하는 해수욕장입니다. 서핑, 카이트보딩 등 다양한 해양 스포츠를 즐길 수 있습니다.', 0.0, 0),
('언양읍성', '울산광역시 울주군 언양읍 동부리 292-1 (44905)', '울산', 6, 172000, '고려시대부터 조선시대까지 사용된 읍성으로, 옛 모습을 잘 간직하고 있습니다. 성곽을 따라 걸으며 역사의 숨결을 느껴보세요.', 0.0, 0),
('함월루', '울산광역시 중구 함월1길 121 (44406)', '울산', 5, 168000, '성안동에 위치한 누각으로, 울산 시내와 동해 바다의 전망을 감상할 수 있는 야경 명소입니다. 로맨틱한 분위기를 즐겨보세요.', 0.0, 0),
('슬도', '울산광역시 동구 방어동 92-1 (44011)', '울산', 4, 135000, '파도가 바위에 부딪힐 때 거문고 소리가 난다고 하여 슬도라 불리는 작은 섬입니다. 방어진항의 아름다운 풍경을 감상해보세요.', 0.0, 0),
('복순도가', '울산광역시 울주군 상북면 향산동길 48 (44953)', '울산', 6, 198000, '전통 방식과 현대적인 기술을 결합하여 만드는 프리미엄 막걸리 양조장입니다. 시음과 함께 막걸리가 만들어지는 과정을 배워보세요.', 0.0, 0),
('작천정 계곡', '울산광역시 울주군 삼남면 작천정길 190 (44950)', '울산', 9, 225000, '영남알프스에서 흘러내리는 맑은 물이 모여 만든 아름다운 계곡입니다. 여름철 물놀이 장소로 인기가 많습니다.', 0.0, 0),
('울산시립미술관', '울산광역시 중구 북정동 1-3 (44539)', '울산', 5, 182000, '미디어아트 중심의 현대미술관으로, 다양한 기획 전시를 선보입니다. 새로운 예술 트렌드를 경험해보세요.', 0.0, 0),
('강동 몽돌해변', '울산광역시 북구 산하중앙2로 11 (44243)', '울산', 7, 208000, '주전 몽돌해변과는 또 다른 매력을 가진 몽돌 해변입니다. 해안도로를 따라 드라이브를 즐기기에도 좋습니다.', 0.0, 0),
('쇠부리체육센터', '울산광역시 북구 달천철장길 57 (44238)', '울산', 8, 218000, '과거 철 생산지였던 달천철장을 기념하여 만든 곳입니다. 쇠부리 축제가 열리면 다양한 전통문화 체험을 할 수 있습니다.', 0.0, 0),
('처용암', '울산광역시 남구 황성동 780-3 (44781)', '울산', 4, 148000, '신라시대 처용설화가 깃든 바위입니다. 울산의 역사와 문화를 이해하는 데 중요한 장소입니다.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('세종호수공원', '세종특별자치시 다솜로 216 (30126)', '세종', 8, 230000, '국내 최대 규모의 인공호수인 세종호수공원 바로 앞에 위치하고 있습니다. 자전거를 타거나 카누를 즐기며 여유로운 시간을 보내세요.', 0.0, 0),
('국립세종수목원', '세종특별자치시 수목원로 136 (30128)', '세종', 7, 210000, '사계절 내내 아름다운 꽃과 나무를 감상할 수 있는 국립세종수목원 인근입니다. 특히 한국전통정원은 꼭 들러보세요.', 0.0, 0),
('정부세종청사 옥상정원', '세종특별자치시 다솜2로 94 (30112)', '세종', 5, 180000, '세계에서 가장 긴 옥상정원으로 기네스북에 등재된 곳입니다. 미리 예약하면 가이드 투어에 참여할 수 있습니다.', 0.0, 0),
('금강보행교 이응다리', '세종특별자치시 세종동 (30128)', '세종', 6, 200000, '한글 자음 "ㅇ" 모양을 형상화한 독특한 디자인의 보행교입니다. 밤이면 화려한 조명이 켜져 아름다운 야경을 선사합니다.', 0.0, 0),
('베어트리파크', '세종특별자치시 전동면 신송로 217 (30001)', '세종', 9, 240000, '1000여 마리의 곰과 아름다운 수목원이 어우러진 테마파크입니다. 아이들과 함께 동물과 교감하는 특별한 시간을 가져보세요.', 0.0, 0),
('대통령기록관', '세종특별자치시 다솜로 250 (30126)', '세종', 4, 160000, '역대 대통령들의 기록물을 보존하고 전시하는 곳입니다. 우리나라 현대사를 이해하는 데 도움이 되는 의미 있는 장소입니다.', 0.0, 0),
('조치원 복숭아마을', '세종특별자치시 연서면 봉암리 (30052)', '세종', 7, 190000, '세종의 특산물인 조치원 복숭아를 맛볼 수 있는 마을입니다. 여름철에는 복숭아 따기 체험도 할 수 있습니다.', 0.0, 0),
('합강캠핑장', '세종특별자치시 연기면 합강공원길 119 (30053)', '세종', 10, 260000, '금강과 미호천이 만나는 곳에 위치한 넓고 쾌적한 캠핑장입니다. 가족, 친구들과 함께 자연 속에서 즐거운 캠핑을 즐겨보세요.', 0.0, 0),
('고복저수지', '세종특별자치시 연서면 고복리 (30051)', '세종', 8, 220000, '낚시터로도 유명한 아름다운 저수지입니다. 주변에 맛집과 카페가 많아 드라이브 코스로도 인기가 좋습니다.', 0.0, 0),
('영평사 구절초', '세종특별자치시 장군면 영평사길 124 (30061)', '세종', 5, 170000, '가을이면 하얀 구절초가 만발하여 장관을 이루는 사찰입니다. 구절초 꽃 축제 기간에 방문하면 더욱 좋습니다.', 0.0, 0),
('뒤웅박고을', '세종특별자치시 전동면 배일길 90-43 (30001)', '세종', 6, 185000, '전통 장류를 주제로 한 테마파크입니다. 장독대가 늘어선 풍경이 정겹고, 한정식 식당의 음식 맛도 일품입니다.', 0.0, 0),
('밀마루전망대', '세종특별자치시 어진동 561 (30109)', '세종', 4, 150000, '행정중심복합도시의 전경을 한눈에 조망할 수 있는 전망대입니다. 도시가 건설되는 과정을 살펴보는 것도 흥미롭습니다.', 0.0, 0),
('국립어린이박물관', '세종특별자치시 어진동 661-1 (30110)', '세종', 7, 205000, '어린이들의 눈높이에 맞춘 다양한 체험 전시가 가득한 곳입니다. 아이들이 마음껏 뛰어놀며 배울 수 있는 공간입니다.', 0.0, 0),
('비암사', '세종특별자치시 전의면 비암사길 137 (30011)', '세종', 5, 165000, '백제 시대에 창건된 것으로 추정되는 오래된 사찰입니다. 국보와 보물 등 많은 문화재를 보유하고 있습니다.', 0.0, 0),
('세종전통시장', '세종특별자치시 조치원읍 조치원8길 42 (30017)', '세종', 8, 195000, '조치원의 중심에 위치한 전통시장입니다. 활기 넘치는 시장 분위기를 느끼고 맛있는 먹거리를 즐겨보세요.', 0.0, 0),
('방축천 음악분수', '세종특별자치시 어진동 (30116)', '세종', 6, 198000, '방축천 수변공원에 설치된 음악분수로, 밤이면 화려한 조명과 함께 아름다운 쇼를 펼칩니다. 산책하며 감상하기 좋습니다.', 0.0, 0),
('연기향교', '세종특별자치시 연기면 교촌1길 15 (30053)', '세종', 4, 145000, '고려시대에 창건된 것으로 전해지는 유서 깊은 향교입니다. 고즈넉한 분위기 속에서 전통 건축의 미를 느껴보세요.', 0.0, 0),
('산림박물관', '세종특별자치시 금남면 산림박물관길 110 (30078)', '세종', 7, 215000, '우리나라의 숲과 나무에 대한 모든 것을 알 수 있는 곳입니다. 다양한 체험 프로그램을 통해 숲의 소중함을 배워보세요.', 0.0, 0),
('조세박물관', '세종특별자치시 국세청로 8-14 (30128)', '세종', 3, 130000, '세금의 역사와 중요성을 배울 수 있는 국립조세박물관입니다. 아이들에게 올바른 납세 의식을 심어줄 수 있습니다.', 0.0, 0),
('교과서박물관', '세종특별자치시 연동면 청연로 492-14 (30050)', '세종', 5, 155000, '우리나라 최초의 교과서 전문 박물관입니다. 옛날 교과서를 보며 학창 시절의 추억을 떠올려보세요.', 0.0, 0),
('운주산성', '세종특별자치시 전동면 송성리 산20-1 (30003)', '세종', 8, 225000, '백제 부흥운동의 거점이었던 것으로 추정되는 산성입니다. 등산을 하며 역사의 현장을 둘러보는 의미 있는 시간을 가져보세요.', 0.0, 0),
('나성동 먹자골목', '세종특별자치시 나성동 775 (30128)', '세종', 6, 208000, '세종시의 신흥 맛집 거리로, 다양한 종류의 음식점들이 모여있습니다. 저녁이면 활기찬 분위기를 느낄 수 있습니다.', 0.0, 0),
('싱싱장터', '세종특별자치시 보람동 626-3 (30151)', '세종', 7, 188000, '세종시 로컬푸드 직매장으로, 신선하고 안전한 농산물을 저렴하게 구입할 수 있습니다. 지역의 건강한 먹거리를 만나보세요.', 0.0, 0),
('전월산', '세종특별자치시 세종동 (30128)', '세종', 5, 175000, '세종시의 전경을 조망하기 좋은 산입니다. 일출과 일몰이 아름다워 사진작가들에게도 인기가 많습니다.', 0.0, 0),
('국립도서관 정책정보관', '세종특별자치시 다솜로 250 (30126)', '세종', 4, 158000, '독특한 디자인의 건물로 유명한 국립세종도서관입니다. 조용한 분위기에서 독서를 즐기며 지적인 휴식을 취해보세요.', 0.0, 0),
('세종예술의전당', '세종특별자치시 나성동 713 (30128)', '세종', 6, 218000, '다양한 장르의 공연이 열리는 세종시의 대표적인 문화예술 공간입니다. 수준 높은 공연과 함께 특별한 밤을 보내세요.', 0.0, 0),
('원수산 습지생태원', '세종특별자치시 연기면 세종리 112-113 (30053)', '세종', 5, 162000, '다양한 수생 동식물을 관찰할 수 있는 자연학습장입니다. 아이들과 함께 생태 탐방을 즐겨보세요.', 0.0, 0),
('오가낭뜰 근린공원', '세종특별자치시 고운동 1385 (30064)', '세종', 8, 202000, '넓은 잔디밭과 놀이터가 있어 아이들이 뛰어놀기 좋은 공원입니다. 가족 단위 나들이 장소로 안성맞춤입니다.', 0.0, 0),
('어진동 한뜰마을', '세종특별자치시 어진동 603 (30110)', '세종', 7, 192000, '정부세종청사와 가까워 공무원들이 많이 거주하는 주거단지입니다. 깔끔하고 정돈된 도시의 모습을 볼 수 있습니다.', 0.0, 0),
('세종중앙공원', '세종특별자치시 세종동 (30128)', '세종', 9, 235000, '세종호수공원과 국립세종수목원을 아우르는 거대한 공원입니다. 도시와 자연이 조화롭게 어우러진 공간에서 진정한 휴식을 경험하세요.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('수원화성 행궁 옆', '경기도 수원시 팔달구 정조로 825 (16257)', '경기', 6, 180000, '유네스코 세계문화유산인 수원화성 행궁 바로 옆에 위치하고 있습니다. 성곽길을 따라 걸으며 조선시대의 역사를 느껴보세요.', 0.0, 0),
('에버랜드 캐빈호스텔', '경기도 용인시 처인구 포곡읍 에버랜드로 199 (17023)', '경기', 8, 280000, '대한민국 대표 테마파크 에버랜드와 가까워 신나게 놀고 편안하게 쉴 수 있습니다. 사파리월드와 로스트밸리는 꼭 경험해보세요.', 0.0, 0),
('파주 헤이리 예술마을', '경기도 파주시 탄현면 헤이리마을길 82-105 (10859)', '경기', 5, 200000, '예술가들이 모여 만든 독특한 문화예술마을입니다. 갤러리, 박물관, 카페 등을 둘러보며 예술적 감성을 충전하세요.', 0.0, 0),
('가평 아침고요수목원', '경기도 가평군 상면 수목원로 432 (12448)', '경기', 7, 220000, '사계절 아름다운 꽃과 나무가 가득한 수목원입니다. 밤이면 오색별빛정원전이 열려 환상적인 분위기를 자아냅니다.', 0.0, 0),
('양평 두물머리', '경기도 양평군 양서면 두물머리길 125 (12585)', '경기', 4, 170000, '북한강과 남한강이 만나는 곳으로, 물안개가 피어오르는 새벽 풍경이 아름답기로 유명합니다. 400년 된 느티나무 아래서 소원을 빌어보세요.', 0.0, 0),
('임진각 평화누리공원', '경기도 파주시 문산읍 임진각로 148-53 (10811)', '경기', 6, 190000, '분단의 아픔과 평화의 소중함을 느낄 수 있는 곳입니다. 바람개비 언덕에서 평화를 기원하며 사진을 찍어보세요.', 0.0, 0),
('한국민속촌', '경기도 용인시 기흥구 민속촌로 90 (17075)', '경기', 8, 240000, '조선시대의 생활 모습을 그대로 재현해 놓은 곳입니다. 사극 드라마의 주인공이 된 듯한 기분을 느껴보세요.', 0.0, 0),
('남한산성', '경기도 광주시 남한산성면 남한산성로 731 (12739)', '경기', 7, 210000, '병자호란의 아픈 역사를 간직한 산성으로, 유네스코 세계유산에 등재되었습니다. 성곽을 따라 트레킹을 하며 역사를 되새겨보세요.', 0.0, 0),
('제부도 바닷길', '경기도 화성시 서신면 제부로 184 (18531)', '경기', 5, 160000, '하루 두 번 바닷길이 열리는 신비로운 섬입니다. 갯벌 체험을 하고 맛있는 조개구이를 즐겨보세요.', 0.0, 0),
('쁘띠프랑스', '경기도 가평군 청평면 호반로 1063 (12456)', '경기', 4, 180000, '프랑스의 작은 마을을 그대로 옮겨놓은 듯한 이국적인 테마파크입니다. 어린왕자를 테마로 한 아기자기한 공간이 많습니다.', 0.0, 0),
('광명동굴', '경기도 광명시 가학로85번길 142 (14341)', '경기', 7, 200000, '폐광을 개발하여 만든 동굴 테마파크입니다. 와인 동굴, 아쿠아월드 등 다양한 볼거리와 즐길 거리가 있습니다.', 0.0, 0),
('포천 아트밸리', '경기도 포천시 신북면 아트밸리로 234 (11139)', '경기', 6, 195000, '폐채석장을 아름다운 문화예술 공간으로 탈바꿈시킨 곳입니다. 천주호의 에메랄드빛 물색이 신비로운 분위기를 자아냅니다.', 0.0, 0),
('이천 도자기마을', '경기도 이천시 신둔면 도자예술로5번길 109 (17301)', '경기', 5, 175000, '도자기의 고장 이천에서 도자기 만들기 체험을 해보세요. 직접 만든 도자기는 특별한 여행 기념품이 될 것입니다.', 0.0, 0),
('안성팜랜드', '경기도 안성시 공도읍 대신두길 28 (17554)', '경기', 9, 250000, '넓은 초원에서 양, 소, 말 등 다양한 가축들과 교감할 수 있는 체험 목장입니다. 계절마다 아름다운 꽃밭이 펼쳐집니다.', 0.0, 0),
('오이도 빨간등대', '경기도 시흥시 오이도로 175 (15011)', '경기', 4, 150000, '서해의 아름다운 일몰을 감상할 수 있는 낭만적인 장소입니다. 등대 앞에서 기념사진을 찍고 맛있는 해물칼국수를 맛보세요.', 0.0, 0),
('자라섬', '경기도 가평군 가평읍 달전리 1-1 (12411)', '경기', 8, 230000, '매년 가을 국제 재즈 페스티벌이 열리는 곳으로 유명합니다. 캠핑을 즐기며 음악과 자연을 만끽하세요.', 0.0, 0),
('율동공원 번지점프', '경기도 성남시 분당구 문정로 77 (13593)', '경기', 3, 130000, '도심 속 공원에서 짜릿한 번지점프를 체험할 수 있습니다. 스트레스를 한 방에 날려버리고 싶은 분들께 추천합니다.', 0.0, 0),
('벽초지수목원', '경기도 파주시 광탄면 부흥로 242 (10949)', '경기', 6, 205000, '동양과 서양의 정원을 모두 감상할 수 있는 아름다운 수목원입니다. 드라마 촬영지로도 자주 등장하는 곳입니다.', 0.0, 0),
('용문사 은행나무', '경기도 양평군 용문면 용문산로 782 (12540)', '경기', 5, 165000, '1100년이 넘는 세월을 살아온 거대한 은행나무가 있는 용문사입니다. 가을이면 노랗게 물든 은행나무가 장관을 이룹니다.', 0.0, 0),
('일산 호수공원', '경기도 고양시 일산동구 호수로 595 (10400)', '경기', 7, 215000, '동양 최대의 인공 호수공원으로, 고양국제꽃박람회가 열리는 곳입니다. 자전거를 타거나 산책을 하며 여유로운 시간을 보내세요.', 0.0, 0),
('화담숲', '경기도 광주시 도척면 도척윗로 278-1 (12813)', '경기', 6, 225000, 'LG상록재단이 운영하는 생태수목원으로, 아름다운 자연을 훼손하지 않고 조성한 것이 특징입니다. 가을 단풍이 특히 아름답습니다.', 0.0, 0),
('스타필드 하남', '경기도 하남시 미사대로 750 (12942)', '경기', 8, 260000, '쇼핑, 레저, 힐링을 한곳에서 즐길 수 있는 복합 쇼핑 테마파크입니다. 아쿠아필드에서 찜질과 스파를 즐겨보세요.', 0.0, 0),
('궁평항', '경기도 화성시 서신면 궁평항로 1049-24 (18530)', '경기', 7, 198000, '싱싱한 수산물을 저렴하게 구입할 수 있는 수산물 직판장이 있습니다. 바다낚시를 즐기기에도 좋은 곳입니다.', 0.0, 0),
('허브아일랜드', '경기도 포천시 신북면 청신로947번길 35 (11137)', '경기', 6, 208000, '허브를 테마로 한 동화 같은 공간입니다. 허브 향기를 맡으며 힐링하고, 밤에는 불빛동화축제를 즐겨보세요.', 0.0, 0),
('광교호수공원', '경기도 수원시 영통구 광교호수로 102 (16512)', '경기', 7, 218000, '아름다운 야경으로 유명한 도심 속 호수공원입니다. 국내 최고의 야경 명소로 손꼽히는 곳에서 낭만적인 밤을 보내세요.', 0.0, 0),
('서울대공원', '경기도 과천시 대공원광장로 102 (13829)', '경기', 10, 270000, '동물원, 식물원, 서울랜드가 함께 있는 종합 공원입니다. 리프트를 타고 올라가 코끼리열차를 타는 재미도 쏠쏠합니다.', 0.0, 0),
('전곡리 선사유적지', '경기도 연천군 전곡읍 평화로443번길 2 (11012)', '경기', 5, 155000, '구석기 시대의 유물이 대량으로 발견된 세계적인 유적지입니다. 선사시대의 생활 모습을 체험하며 역사를 배워보세요.', 0.0, 0),
('프로방스 마을', '경기도 파주시 탄현면 새오지로 69 (10859)', '경기', 4, 178000, '남프랑스의 프로방스 지방을 테마로 꾸민 아기자기한 마을입니다. 예쁜 상점과 레스토랑이 많아 데이트 코스로 인기가 많습니다.', 0.0, 0),
('융건릉', '경기도 화성시 효행로481번길 21 (18324)', '경기', 6, 182000, '조선 정조와 그의 아버지 사도세자의 무덤이 있는 곳입니다. 울창한 소나무 숲길을 산책하며 경건한 마음으로 참배해보세요.', 0.0, 0),
('아인스월드', '경기도 부천시 도약로 1 (14553)', '경기', 7, 192000, '세계 각국의 유명 건축물들을 미니어처로 만들어 놓은 테마파크입니다. 세계 일주를 하는 듯한 기분을 느껴보세요.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('속초 중앙시장', '강원도 속초시 중앙로147번길 12 (24832)', '강원', 6, 170000, '만석닭강정, 아바이순대 등 맛있는 먹거리가 가득한 속초의 대표적인 전통시장입니다. 활기 넘치는 시장에서 강원도의 맛을 즐겨보세요.', 0.0, 0),
('강릉 경포대', '강원도 강릉시 경포로 365 (25461)', '강원', 7, 200000, '동해안 최대의 해변인 경포해수욕장과 아름다운 경포호가 있는 곳입니다. 관동팔경 중 으뜸으로 꼽히는 경포대의 정취를 느껴보세요.', 0.0, 0),
('춘천 남이섬', '강원도 춘천시 남산면 남이섬길 1 (24464)', '강원', 5, 190000, '드라마 겨울연가의 촬영지로 유명해진 아름다운 섬입니다. 메타세쿼이아길을 걸으며 낭만적인 추억을 만들어보세요.', 0.0, 0),
('평창 양떼목장', '강원도 평창군 대관령면 대관령마루길 483-32 (25342)', '강원', 8, 220000, '푸른 초원에서 한가롭게 풀을 뜯는 양들을 만날 수 있는 곳입니다. 건초 주기 체험을 하며 양들과 교감해보세요.', 0.0, 0),
('설악산 국립공원', '강원도 속초시 설악산로 1091 (24903)', '강원', 10, 260000, '웅장한 기암괴석과 맑은 계곡이 어우러진 우리나라의 명산입니다. 케이블카를 타고 권금성에 올라 비경을 감상해보세요.', 0.0, 0),
('정동진 해돋이', '강원도 강릉시 강동면 정동진리 (25631)', '강원', 6, 180000, '세계에서 바다와 가장 가까운 역으로 기네스북에 오른 정동진역에서 해돋이를 감상해보세요. 모래시계공원도 함께 둘러보세요.', 0.0, 0),
('삼척 해상케이블카', '강원도 삼척시 근덕면 용화해변길 23 (25511)', '강원', 7, 210000, '장호항과 용화해변을 잇는 해상케이블카를 타고 동해의 푸른 바다를 감상해보세요. 바닥이 투명하여 아찔한 스릴을 느낄 수 있습니다.', 0.0, 0),
('동해 촛대바위', '강원도 동해시 추암동 (25785)', '강원', 5, 160000, '애국가 첫 소절의 배경 화면으로 유명한 촛대바위 위로 떠오르는 일출이 장관입니다. 주변의 기암괴석들도 함께 감상해보세요.', 0.0, 0),
('인제 자작나무숲', '강원도 인제군 인제읍 원대리 763-4 (24631)', '강원', 6, 195000, '하얀 자작나무가 빽빽하게 들어선 이국적인 풍경의 숲입니다. 겨울이면 눈 덮인 풍경이 더욱 아름다워 동화 속에 들어온 듯한 기분을 선사합니다.', 0.0, 0),
('고성 통일전망대', '강원도 고성군 현내면 금강산로 481 (24711)', '강원', 8, 200000, '분단의 현실을 직접 눈으로 확인할 수 있는 최북단 전망대입니다. 금강산과 해금강의 절경을 바라보며 평화통일을 기원해보세요.', 0.0, 0),
('영월 한반도지형', '강원도 영월군 한반도면 옹정리 875 (26213)', '강원', 7, 185000, '서강의 물줄기가 휘돌아가며 만들어낸 한반도 모양의 지형입니다. 뗏목 체험을 하며 신비로운 자연의 작품을 감상해보세요.', 0.0, 0),
('정선 레일바이크', '강원도 정선군 여량면 노추산로 745 (26120)', '강원', 8, 230000, '폐선된 철길을 따라 달리며 정선의 아름다운 풍경을 감상할 수 있습니다. 터널을 지날 때마다 펼쳐지는 화려한 조명이 즐거움을 더합니다.', 0.0, 0),
('원주 뮤지엄 산', '강원도 원주시 지정면 오크밸리2길 260 (26357)', '강원', 5, 240000, '세계적인 건축가 안도 타다오가 설계한 뮤지엄으로, 자연과 예술이 조화를 이룹니다. 제임스 터렐의 작품은 놓치지 마세요.', 0.0, 0),
('홍천 알파카월드', '강원도 홍천군 화촌면 풍천리 310 (25158)', '강원', 9, 250000, '귀여운 알파카와 함께 숲속을 산책하고 먹이 주기 체험을 할 수 있는 곳입니다. 아이들에게 잊지 못할 추억을 선물하세요.', 0.0, 0),
('양양 서피비치', '강원도 양양군 현북면 하조대해안길 119 (25043)', '강원', 6, 215000, '서핑 전용 해변과 이국적인 분위기의 펍, 라운지가 있는 서퍼들의 천국입니다. 서핑 강습을 받고 신나는 파티를 즐겨보세요.', 0.0, 0),
('태백산 눈꽃축제', '강원도 태백시 태백산로 4778 (26011)', '강원', 7, 198000, '겨울이면 순백의 눈꽃이 장관을 이루는 태백산에서 눈꽃축제가 열립니다. 거대한 눈 조각들을 감상하고 눈썰매를 즐겨보세요.', 0.0, 0),
('오죽헌', '강원도 강릉시 율곡로3139번길 24 (25477)', '강원', 5, 150000, '신사임당과 율곡 이이가 태어난 유서 깊은 곳입니다. 검은 대나무가 자라는 오죽헌에서 역사 속 인물들의 숨결을 느껴보세요.', 0.0, 0),
('화천 산천어축제', '강원도 화천군 화천읍 (24121)', '강원', 8, 225000, '매년 겨울, 세계적인 겨울 축제인 화천 산천어축제가 열립니다. 얼음낚시로 산천어를 잡고 맨손잡기 체험도 즐겨보세요.', 0.0, 0),
('철원 DMZ 평화관광', '강원도 철원군 동송읍 태봉로 1825 (24010)', '강원', 6, 175000, '제2땅굴, 월정리역 등 분단의 현장을 둘러보는 안보관광 코스입니다. 평화전망대에서 비무장지대의 자연을 조망해보세요.', 0.0, 0),
('횡성 한우마을', '강원도 횡성군 횡성읍 횡성로 337 (25228)', '강원', 7, 280000, '대한민국 대표 한우 브랜드인 횡성한우를 맛볼 수 있는 곳입니다. 품질 좋은 한우를 저렴하게 구입하고 맛있는 구이를 즐겨보세요.', 0.0, 0),
('묵호등대 논골담길', '강원도 동해시 묵호진동 2-219 (25719)', '강원', 4, 140000, '묵호항 언덕에 자리 잡은 마을의 담벼락에 아기자기한 벽화가 그려져 있습니다. 골목길을 따라 걸으며 어촌 마을의 소박한 삶을 느껴보세요.', 0.0, 0),
('대관령 하늘목장', '강원도 평창군 대관령면 꽃밭양지길 458-23 (25342)', '강원', 9, 245000, '서울 여의도 면적의 4배에 달하는 광활한 초지 목장입니다. 트랙터 마차를 타고 정상에 올라 풍력발전기와 함께 펼쳐진 풍경을 감상하세요.', 0.0, 0),
('하이원리조트', '강원도 정선군 고한읍 하이원길 265 (26155)', '강원', 8, 290000, '스키, 골프, 카지노, 워터파크 등 사계절 내내 즐길 거리가 가득한 복합 리조트입니다. 해발 1,340m에서 즐기는 스키는 짜릿함을 선사합니다.', 0.0, 0),
('주문진항', '강원도 강릉시 주문진읍 해안로 1758 (25421)', '강원', 7, 188000, '드라마 도깨비 촬영지로 유명해진 방사제가 있는 곳입니다. 싱싱한 해산물을 맛보고 활기찬 어시장을 구경해보세요.', 0.0, 0),
('월정사 전나무숲길', '강원도 평창군 진부면 오대산로 374-8 (25318)', '강원', 6, 192000, '천년의 역사를 간직한 월정사로 들어가는 길에 아름다운 전나무숲이 있습니다. 맑은 공기를 마시며 걷는 것만으로도 힐링이 됩니다.', 0.0, 0),
('환선굴', '강원도 삼척시 신기면 환선로 800 (25620)', '강원', 8, 218000, '국내 최대 규모의 석회암 동굴로, 동굴 내부가 웅장하고 신비롭습니다. 모노레일을 타고 편하게 동굴 입구까지 갈 수 있습니다.', 0.0, 0),
('낙산사', '강원도 양양군 강현면 낙산사로 100 (25008)', '강원', 5, 168000, '동해 바다가 내려다보이는 절벽 위에 자리 잡은 아름다운 사찰입니다. 의상대에서 바라보는 일출이 특히 유명합니다.', 0.0, 0),
('청령포', '강원도 영월군 남면 광천리 508-1 (26214)', '강원', 4, 145000, '조선시대 단종의 유배지로, 삼면이 강으로 둘러싸여 있고 한쪽은 험준한 절벽으로 막혀있습니다. 배를 타고 들어가야 하는 슬픈 역사의 장소입니다.', 0.0, 0),
('하슬라아트월드', '강원도 강릉시 강동면 율곡로 1441 (25631)', '강원', 6, 235000, '자연과 예술이 어우러진 복합예술공간입니다. 바다를 배경으로 한 야외 조각공원이 특히 인상적입니다.', 0.0, 0),
('알펜시아 리조트', '강원도 평창군 대관령면 솔봉로 325 (25351)', '강원', 9, 270000, '2018 평창 동계올림픽의 주 무대였던 곳입니다. 스키점프대에 올라 선수들이 느꼈을 짜릿함을 상상해보세요.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('단양 만천하스카이워크', '충청북도 단양군 적성면 애곡리 94 (27014)', '충북', 7, 200000, '남한강 절벽 위에서 하늘 길을 걷는 듯한 스릴을 느낄 수 있습니다. 짚와이어와 알파인코스터도 함께 즐겨보세요.', 0.0, 0),
('제천 청풍호반케이블카', '충청북도 제천시 청풍면 청풍명월로 879-17 (27211)', '충북', 8, 220000, '내륙의 바다라 불리는 청풍호의 아름다운 풍경을 감상할 수 있습니다. 비봉산 정상에서 바라보는 풍경이 일품입니다.', 0.0, 0),
('보은 법주사', '충청북도 보은군 속리산면 법주사로 405 (28966)', '충북', 6, 180000, '속리산에 위치한 천년고찰로, 국보인 팔상전과 금동미륵대불이 유명합니다. 유네스코 세계유산에 등재된 산사의 정취를 느껴보세요.', 0.0, 0),
('충주호 유람선', '충청북도 충주시 동량면 지등로 888 (27471)', '충북', 9, 230000, '충주호의 아름다운 풍경과 옥순봉, 구담봉의 기암절벽을 유람선을 타고 감상해보세요. 신선이 된 듯한 기분을 느낄 수 있습니다.', 0.0, 0),
('영동 와인코리아', '충청북도 영동군 영동읍 영동황간로 662 (29132)', '충북', 5, 170000, '국내 최대의 와인 산지인 영동에서 와인 족욕과 시음 체험을 즐겨보세요. 와인 터널에서 숙성되는 와인을 구경하는 재미도 있습니다.', 0.0, 0),
('괴산 산막이옛길', '충청북도 괴산군 칠성면 산막이옛길 88 (28022)', '충북', 7, 190000, '괴산호를 따라 조성된 아름다운 산책길입니다. 유람선을 타고 주변 경치를 감상하는 것도 좋은 방법입니다.', 0.0, 0),
('청주 상당산성', '충청북도 청주시 상당구 성내로124번길 34 (28521)', '충북', 8, 210000, '삼국시대부터 있었던 것으로 추정되는 오래된 산성입니다. 성곽을 따라 걸으며 청주 시내를 조망하고, 맛있는 파전과 막걸리를 즐겨보세요.', 0.0, 0),
('단양 도담삼봉', '충청북도 단양군 매포읍 삼봉로 644-33 (27011)', '충북', 6, 185000, '남한강 위에 솟아오른 세 개의 봉우리로, 단양팔경 중 으뜸으로 꼽힙니다. 정도전이 자신의 호를 삼봉이라 지었을 만큼 아름다운 곳입니다.', 0.0, 0),
('옥천 장계관광지', '충청북도 옥천군 안내면 장계리 24-1 (29007)', '충북', 7, 195000, '대청호반에 위치한 관광지로, 시인 정지용의 생가와 문학관이 있습니다. 향수 30리. 자전거를 타고 아름다운 시의 고장을 달려보세요.', 0.0, 0),
('진천 농다리', '충청북도 진천군 문백면 농다리길 1032-35 (27863)', '충북', 5, 160000, '천년의 세월을 견뎌온 돌다리로, 독특한 축조 방식이 신비로움을 더합니다. 다리를 건너며 소원을 빌어보세요.', 0.0, 0),
('음성 감곡매괴성모순례지', '충청북도 음성군 감곡면 성당길 10 (27610)', '충북', 4, 150000, '고딕 양식의 아름다운 성당 건물로 유명한 천주교 성지입니다. 경건한 분위기 속에서 마음의 평화를 찾아보세요.', 0.0, 0),
('증평 벨포레리조트', '충청북도 증평군 도안면 벨포레길 346 (27914)', '충북', 10, 280000, '골프, 루지, 요트, 양떼목장 등 다양한 레저 시설을 갖춘 복합 리조트입니다. 가족 모두가 즐거운 시간을 보낼 수 있습니다.', 0.0, 0),
('수암골 벽화마을', '충청북도 청주시 상당구 수암로58번길 14 (28530)', '충북', 3, 130000, '드라마 촬영지로 유명해진 달동네 벽화마을입니다. 골목길을 따라 정겨운 벽화를 감상하고, 전망 좋은 카페에서 차 한잔의 여유를 즐겨보세요.', 0.0, 0),
('월악산 국립공원', '충청북도 제천시 한수면 미륵송계로 1647 (27213)', '충북', 9, 240000, '웅장한 산세와 아름다운 계곡을 자랑하는 국립공원입니다. 등산을 하며 자연의 위대함을 느껴보세요.', 0.0, 0),
('고수동굴', '충청북도 단양군 단양읍 고수동굴길 8 (27021)', '충북', 7, 205000, '다양한 종류의 종유석과 석순이 신비로운 지하 궁전을 이루고 있습니다. 자연이 만들어낸 위대한 예술 작품을 감상해보세요.', 0.0, 0),
('청남대', '충청북도 청주시 상당구 문의면 청남대길 646 (28193)', '충북', 8, 250000, '대통령들의 별장으로 사용되었던 곳으로, 남쪽의 청와대라 불립니다. 아름다운 정원과 대통령 역사문화관을 둘러보세요.', 0.0, 0),
('화양계곡', '충청북도 괴산군 청천면 화양로 582 (28024)', '충북', 8, 215000, '속리산 국립공원에 속한 아름다운 계곡입니다. 맑은 물과 기암괴석이 어우러져 여름철 피서지로 인기가 많습니다.', 0.0, 0),
('옥순봉 출렁다리', '충청북도 제천시 수산면 옥순봉로 342 (27221)', '충북', 6, 198000, '청풍호 위를 걷는 듯한 아찔함을 느낄 수 있는 출렁다리입니다. 옥순봉의 절경을 가까이서 감상할 수 있습니다.', 0.0, 0),
('문의문화재단지', '충청북도 청주시 상당구 문의면 대청호반로 721 (28194)', '충북', 7, 188000, '대청호 건설로 수몰될 위기에 처했던 지역의 문화재들을 이전, 복원해 놓은 곳입니다. 옛 선조들의 생활 모습을 엿볼 수 있습니다.', 0.0, 0),
('반기문 평화랜드', '충청북도 음성군 원남면 행치길 12 (27650)', '충북', 5, 162000, 'UN 사무총장을 지낸 반기문의 생가와 기념관이 있는 곳입니다. 세계 평화를 위해 노력한 그의 삶을 돌아보는 시간을 가져보세요.', 0.0, 0),
('충주 중앙탑', '충청북도 충주시 중앙탑면 중앙탑길 112-28 (27464)', '충북', 6, 172000, '통일신라시대에 세워진 7층 석탑으로, 국보 제6호입니다. 우리나라 중앙에 위치한다고 하여 중앙탑이라 불립니다.', 0.0, 0),
('보탑사', '충청북도 진천군 진천읍 김유신길 641 (27845)', '충북', 5, 158000, '3층 목탑으로 유명한 비구니 사찰입니다. 못을 사용하지 않고 전통 방식으로 지은 목탑의 아름다움을 감상해보세요.', 0.0, 0),
('라바랜드', '충청북도 충주시 남한강로 30 (27338)', '충북', 8, 225000, '인기 애니메이션 캐릭터 라바를 테마로 한 어린이 놀이공원입니다. 아이들이 좋아하는 다양한 놀이기구가 있습니다.', 0.0, 0),
('수안보 온천', '충청북도 충주시 수안보면 온천중앙길 36 (27464)', '충북', 7, 208000, '오랜 역사를 자랑하는 왕의 온천입니다. 수질이 좋기로 유명하며, 다양한 온천 시설에서 편안한 휴식을 즐길 수 있습니다.', 0.0, 0),
('옥계폭포', '충청북도 영동군 심천면 옥계폭포길 50 (29153)', '충북', 4, 140000, '박연폭포라고도 불리는 아름다운 폭포입니다. 시원하게 떨어지는 물줄기를 보며 더위를 잊어보세요.', 0.0, 0),
('국립청주박물관', '충청북도 청주시 상당구 명암로 143 (28522)', '충북', 6, 178000, '충북 지역의 역사와 문화를 한눈에 볼 수 있는 곳입니다. 다양한 유물을 통해 선조들의 지혜를 배워보세요.', 0.0, 0),
('미동산수목원', '충청북도 청주시 상당구 미원면 수목원길 51 (28164)', '충북', 8, 218000, '다양한 종류의 나무와 식물들이 자라는 아름다운 수목원입니다. 숲 해설 프로그램을 통해 자연에 대해 더 깊이 이해할 수 있습니다.', 0.0, 0),
('사인암', '충청북도 단양군 대강면 사인암2길 42 (27005)', '충북', 5, 168000, '단양팔경 중 하나로, 병풍처럼 펼쳐진 기암절벽이 장관입니다. 고려시대 학자 우탁이 사인 벼슬에 있을 때 이곳에 머물렀다고 합니다.', 0.0, 0),
('조정지댐', '충청북도 충주시 동량면 조동리 (27471)', '충북', 7, 192000, '카누, 카약 등 수상 스포츠를 즐기기에 좋은 곳입니다. 아름다운 풍경 속에서 짜릿한 레저를 즐겨보세요.', 0.0, 0),
('육거리종합시장', '충청북도 청주시 상당구 청남로2153번길 20 (28711)', '충북', 8, 202000, '청주에서 가장 크고 활기 넘치는 전통시장입니다. 다양한 물건을 구경하고 맛있는 시장 음식을 즐기는 재미가 있습니다.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('보령 대천해수욕장', '충청남도 보령시 해수욕장4길 69 (33418)', '충남', 8, 220000, '서해안 최대의 해수욕장으로, 매년 여름 머드축제가 열리는 곳입니다. 머드를 바르고 신나게 축제를 즐겨보세요.', 0.0, 0),
('태안 안면도 꽃지해변', '충청남도 태안군 안면읍 해안관광로 3126 (32169)', '충남', 7, 200000, '할미, 할아비 바위 사이로 떨어지는 일몰이 아름답기로 유명한 곳입니다. 낭만적인 풍경을 감상하며 특별한 추억을 만들어보세요.', 0.0, 0),
('공주 공산성', '충청남도 공주시 웅진로 280 (32549)', '충남', 6, 180000, '백제의 도읍이었던 웅진을 지키던 산성으로, 유네스코 세계유산에 등재되었습니다. 성곽을 따라 걸으며 금강의 아름다운 풍경을 감상하세요.', 0.0, 0),
('부여 백제문화단지', '충청남도 부여군 규암면 백제문로 455 (33116)', '충남', 9, 240000, '백제의 마지막 도읍이었던 사비 시대의 왕궁과 생활 모습을 재현한 곳입니다. 사비궁의 웅장함과 능사의 아름다움을 느껴보세요.', 0.0, 0),
('서산 해미읍성', '충청남도 서산시 해미면 남문2로 143 (31974)', '충남', 7, 190000, '조선시대의 대표적인 읍성으로, 원형이 잘 보존되어 있습니다. 천주교 박해의 아픈 역사를 간직한 곳이기도 합니다.', 0.0, 0),
('예산 수덕사', '충청남도 예산군 덕산면 수덕사안길 79 (32418)', '충남', 5, 170000, '현존하는 가장 오래된 목조 건물인 대웅전(국보 제49호)이 있는 유서 깊은 사찰입니다. 고즈넉한 산사의 정취를 느껴보세요.', 0.0, 0),
('당진 삽교호 함상공원', '충청남도 당진시 신평면 삽교천3길 79 (31741)', '충남', 8, 210000, '퇴역한 군함을 활용하여 만든 함상공원입니다. 해군과 해병대의 생활상을 엿보고 다양한 군장비를 체험할 수 있습니다.', 0.0, 0),
('천안 독립기념관', '충청남도 천안시 동남구 목천읍 독립기념관로 1 (31232)', '충남', 10, 250000, '우리 민족의 독립운동사를 한눈에 볼 수 있는 곳입니다. 겨레의 탑 앞에서 순국선열들의 숭고한 희생을 기려보세요.', 0.0, 0),
('아산 현충사', '충청남도 아산시 염치읍 현충사길 126 (31478)', '충남', 6, 175000, '충무공 이순신 장군의 사당입니다. 장군의 영정 앞에서 나라 사랑의 정신을 되새겨보는 의미 있는 시간을 가져보세요.', 0.0, 0),
('논산 선샤인랜드', '충청남도 논산시 연무읍 봉황로 102 (33004)', '충남', 7, 205000, '드라마 미스터 션샤인의 촬영 세트장으로, 1950년대 풍경을 재현해 놓았습니다. 서바이벌 게임과 밀리터리 체험도 즐길 수 있습니다.', 0.0, 0),
('금산 하늘물빛정원', '충청남도 금산군 추부면 검한1길 156 (32700)', '충남', 8, 230000, '아름다운 정원과 허브 족욕, 찜질방 등을 즐길 수 있는 힐링 테마파크입니다. 밤이면 화려한 조명이 켜져 더욱 아름답습니다.', 0.0, 0),
('서천 국립생태원', '충청남도 서천군 마서면 금강로 1210 (33657)', '충남', 9, 235000, '전 세계 5대 기후를 재현한 에코리움에서 다양한 동식물을 만날 수 있습니다. 아이들과 함께 생태계의 소중함을 배워보세요.', 0.0, 0),
('청양 알프스마을', '충청남도 청양군 정산면 천장호길 223-35 (33311)', '충남', 7, 195000, '겨울이면 얼음분수 축제가 열리는 곳으로 유명합니다. 눈썰매와 봅슬레이를 즐기고, 밤에는 별빛 터널을 걸어보세요.', 0.0, 0),
('홍성 남당항', '충청남도 홍성군 서부면 남당항로 213 (32261)', '충남', 8, 215000, '가을이면 대하 축제가 열리는 미식의 항구입니다. 싱싱한 대하와 함께 다양한 해산물을 맛보세요.', 0.0, 0),
('계룡산 국립공원', '충청남도 공주시 반포면 동학사1로 327-6 (32566)', '충남', 10, 260000, '풍수지리적으로 명당으로 꼽히는 명산입니다. 동학사와 갑사 등 유서 깊은 사찰을 품고 있습니다.', 0.0, 0),
('무령왕릉', '충청남도 공주시 왕릉로 37-2 (32537)', '충남', 5, 160000, '백제 25대 무령왕과 왕비의 무덤으로, 도굴되지 않은 채 발견되어 많은 국보급 유물이 출토되었습니다. 백제 문화의 정수를 느껴보세요.', 0.0, 0),
('낙화암', '충청남도 부여군 부여읍 부소로 1 (33115)', '충남', 4, 150000, '백제가 멸망할 때 삼천궁녀가 몸을 던졌다는 슬픈 전설이 깃든 바위입니다. 유람선을 타고 백마강에서 바라보는 풍경이 아름답습니다.', 0.0, 0),
('신두리 해안사구', '충청남도 태안군 원북면 신두해변길 201-54 (32104)', '충남', 6, 185000, '우리나라 최대 규모의 해안사구로, 마치 사막에 온 듯한 이국적인 풍경을 자아냅니다. 천연기념물로 지정된 소중한 자연유산입니다.', 0.0, 0),
('간월암', '충청남도 서산시 부석면 간월도1길 119-29 (31982)', '충남', 3, 140000, '하루 두 번 썰물 때만 길이 열리는 작은 암자입니다. 물 위에 떠 있는 듯한 신비로운 풍경을 감상해보세요.', 0.0, 0),
('아산 외암민속마을', '충청남도 아산시 송악면 외암민속길 9 (31521)', '충남', 8, 225000, '조선시대 양반촌의 모습을 그대로 간직하고 있는 마을입니다. 고택과 돌담길을 따라 걸으며 옛 정취를 느껴보세요.', 0.0, 0),
('궁남지', '충청남도 부여군 부여읍 궁남로 52 (33119)', '충남', 7, 198000, '우리나라 최초의 인공 연못으로, 백제 무왕의 서동요 전설이 깃든 곳입니다. 여름이면 연꽃이 만발하여 장관을 이룹니다.', 0.0, 0),
('만리포해수욕장', '충청남도 태안군 소원면 만리포2길 180 (32121)', '충남', 9, 238000, '서해안 3대 해수욕장 중 하나로, 넓은 백사장과 울창한 송림이 어우러져 있습니다. 해수욕과 함께 갯벌 체험도 즐겨보세요.', 0.0, 0),
('공세리성당', '충청남도 아산시 인주면 공세리성당길 10 (31412)', '충남', 5, 168000, '120년 역사를 지닌 아름다운 성당으로, 영화와 드라마 촬영지로도 유명합니다. 고딕 양식의 건축물과 아름다운 풍경이 조화를 이룹니다.', 0.0, 0),
('칠갑산', '충청남도 청양군 대치면 한티고개길 174 (33314)', '충남', 8, 208000, '콩밭 매는 아낙네야 노래로 유명한 칠갑산입니다. 등산을 하며 아름다운 자연을 만끽하고, 장곡사에 들러보세요.', 0.0, 0),
('보령 스카이바이크', '충청남도 보령시 해수욕장10길 7 (33418)', '충남', 4, 182000, '대천해수욕장 해변을 따라 달리며 바다 풍경을 감상할 수 있는 레일바이크입니다. 시원한 바닷바람을 맞으며 즐거운 시간을 보내세요.', 0.0, 0),
('유관순열사 사적지', '충청남도 천안시 동남구 병천면 유관순길 38 (31244)', '충남', 6, 172000, '3.1 운동의 상징인 유관순 열사의 생가와 기념관이 있는 곳입니다. 열사의 숭고한 애국정신을 기려보세요.', 0.0, 0),
('성주산자연휴양림', '충청남도 보령시 성주면 성주산로 501-195 (33441)', '충남', 10, 245000, '울창한 숲과 맑은 계곡이 있는 자연휴양림입니다. 숲속의 집에서 편안하게 쉬며 삼림욕을 즐겨보세요.', 0.0, 0),
('정림사지 5층 석탑', '충청남도 부여군 부여읍 정림로 83 (33118)', '충남', 5, 158000, '백제 시대의 대표적인 석탑으로, 세련되고 안정적인 조형미를 자랑합니다. 국보 제9호로 지정되어 있습니다.', 0.0, 0),
('아그로랜드 태신목장', '충청남도 예산군 고덕면 상몽2길 231 (32405)', '충남', 9, 242000, '넓은 초원에서 젖소와 교감하고 다양한 체험을 할 수 있는 목장입니다. 트랙터 열차를 타고 목장을 둘러보는 재미가 있습니다.', 0.0, 0),
('왜목마을', '충청남도 당진시 석문면 왜목길 26 (31748)', '충남', 7, 202000, '일출과 일몰, 월출까지 한 곳에서 감상할 수 있는 특별한 장소입니다. 해변을 따라 산책하며 낭만적인 시간을 보내세요.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('전주 한옥마을', '전라북도 전주시 완산구 기린대로 99 (55042)', '전북', 6, 190000, '700여 채의 한옥이 모여있는 국내 최대 규모의 한옥마을입니다. 한복을 입고 경기전과 전동성당을 둘러보며 시간 여행을 떠나보세요.', 0.0, 0),
('군산 근대역사거리', '전라북도 군산시 해망로 240 (54000)', '전북', 5, 170000, '일제강점기 시대의 아픔과 근대 문화유산이 공존하는 곳입니다. 초원사진관, 히로쓰 가옥 등을 둘러보며 역사를 되새겨보세요.', 0.0, 0),
('남원 광한루원', '전라북도 남원시 요천로 1447 (55783)', '전북', 7, 200000, '춘향전의 무대가 된 아름다운 정원입니다. 오작교를 건너며 춘향과 이몽룡의 사랑 이야기를 떠올려보세요.', 0.0, 0),
('진안 마이산', '전라북도 진안군 마령면 마이산로 130 (55444)', '전북', 8, 220000, '말의 귀를 닮은 두 개의 봉우리가 신비로운 산입니다. 바람에도 쓰러지지 않는 80여 개의 돌탑이 있는 탑사가 유명합니다.', 0.0, 0),
('부안 변산반도', '전라북도 부안군 변산면 변산해변로 79 (56337)', '전북', 9, 240000, '산과 바다가 어우러진 아름다운 국립공원입니다. 채석강의 해식동굴과 격포해수욕장의 일몰이 특히 아름답습니다.', 0.0, 0),
('고창 읍성', '전라북도 고창군 고창읍 읍내리 126 (56048)', '전북', 7, 195000, '조선시대의 읍성으로, 원형이 잘 보존되어 있습니다. 매년 가을 열리는 모양성제 기간에는 답성놀이를 체험할 수 있습니다.', 0.0, 0),
('임실치즈테마파크', '전라북도 임실군 성수면 도인2길 50 (55914)', '전북', 8, 230000, '우리나라 치즈의 원조, 임실에서 치즈 만들기 체험을 즐겨보세요. 유럽풍의 아름다운 건물과 넓은 초원이 이국적인 분위기를 자아냅니다.', 0.0, 0),
('무주 덕유산', '전라북도 무주군 설천면 만선로 185 (55556)', '전북', 10, 280000, '겨울이면 눈꽃과 상고대가 아름다운 설경을 자랑하는 곳입니다. 곤돌라를 타고 설천봉에 올라 쉽게 정상의 풍경을 감상할 수 있습니다.', 0.0, 0),
('익산 미륵사지', '전라북도 익산시 금마면 미륵사지로 362 (57080)', '전북', 6, 180000, '백제 최대의 사찰이었던 미륵사 터로, 유네스코 세계유산에 등재되었습니다. 국보 제11호인 미륵사지 석탑의 웅장함을 느껴보세요.', 0.0, 0),
('정읍 내장산', '전라북도 정읍시 내장산로 936 (56141)', '전북', 9, 250000, '가을 단풍이 아름답기로 유명한 국립공원입니다. 우화정 주변의 단풍 터널은 내장산 단풍의 백미로 꼽힙니다.', 0.0, 0),
('완주 삼례문화예술촌', '전라북도 완주군 삼례읍 삼례역로 81-13 (55341)', '전북', 4, 150000, '일제강점기 양곡창고를 리모델링하여 만든 복합문화공간입니다. 갤러리, 북카페, 소극장 등 다양한 문화시설이 있습니다.', 0.0, 0),
('순창 고추장마을', '전라북도 순창군 순창읍 백산리 265-2 (56018)', '전북', 7, 205000, '순창의 명물, 전통 고추장을 만드는 과정을 보고 체험할 수 있는 마을입니다. 맛있는 고추장과 장아찌를 맛보세요.', 0.0, 0),
('김제 벽골제', '전라북도 김제시 부량면 벽골제로 442 (54421)', '전북', 8, 210000, '우리나라에서 가장 오래된 저수지로, 농경문화의 역사를 엿볼 수 있습니다. 매년 가을 지평선축제가 열려 다양한 체험을 즐길 수 있습니다.', 0.0, 0),
('장수 승마체험장', '전라북도 장수군 장수읍 논개사당길 51 (55628)', '전북', 6, 185000, '말의 고장 장수에서 승마 체험을 즐겨보세요. 전문 교관의 지도 아래 안전하게 말을 타는 법을 배울 수 있습니다.', 0.0, 0),
('선유도', '전라북도 군산시 옥도면 선유도리 (54000)', '전북', 7, 215000, '고군산군도의 중심 섬으로, 아름다운 풍경 덕분에 신선이 노니는 섬이라 불립니다. 자전거나 스쿠터를 타고 섬을 둘러보세요.', 0.0, 0),
('덕진공원', '전라북도 전주시 덕진구 권삼득로 390 (54900)', '전북', 5, 160000, '여름이면 연꽃이 호수를 가득 메우는 아름다운 공원입니다. 연화교를 건너며 낭만적인 분위기를 즐겨보세요.', 0.0, 0),
('지리산 뱀사골계곡', '전라북도 남원시 산내면 와운길 10 (55736)', '전북', 9, 235000, '지리산 국립공원에 속한 아름다운 계곡입니다. 맑고 차가운 물에 발을 담그고 여름 더위를 식혀보세요.', 0.0, 0),
('고창 고인돌유적', '전라북도 고창군 고창읍 고인돌공원길 74 (56048)', '전북', 6, 175000, '세계 최대 규모의 고인돌 군집지로, 유네스코 세계유산에 등재되었습니다. 선사시대로 시간 여행을 떠나보세요.', 0.0, 0),
('채석강', '전라북도 부안군 변산면 격포리 301-1 (56337)', '전북', 8, 225000, '수만 권의 책을 쌓아놓은 듯한 퇴적암층이 장관을 이루는 해안 절벽입니다. 썰물 때 드러나는 해식동굴이 신비로움을 더합니다.', 0.0, 0),
('전주동물원', '전라북도 전주시 덕진구 소리로 68 (54896)', '전북', 8, 208000, '지방 동물원 중 유일하게 코끼리를 보유하고 있는 곳입니다. 다양한 동물들을 만나고 놀이시설도 즐겨보세요.', 0.0, 0),
('모악산', '전라북도 완주군 구이면 모악산길 100 (55364)', '전북', 7, 198000, '김제평야와 만경평야를 굽어보는 호남의 명산입니다. 금산사와 대원사 등 유서 깊은 사찰을 품고 있습니다.', 0.0, 0),
('군산CC', '전라북도 군산시 옥서면 남군산로 1363 (54162)', '전북', 4, 290000, '세계 최대 규모인 81홀 골프 코스를 자랑하는 곳입니다. 갈대밭과 호수가 어우러진 아름다운 코스에서 라운딩을 즐겨보세요.', 0.0, 0),
('강천산', '전라북도 순창군 팔덕면 강천산길 270 (56041)', '전북', 8, 218000, '사계절 내내 아름다운 풍경을 자랑하는 군립공원입니다. 아찔한 구름다리를 건너며 병풍폭포의 절경을 감상해보세요.', 0.0, 0),
('옥정호', '전라북도 임실군 운암면 (55931)', '전북', 7, 192000, '물안개가 아름다운 호수로, 국사봉 전망대에서 바라보는 풍경이 유명합니다. 붕어섬의 독특한 모습도 감상해보세요.', 0.0, 0),
('금산사', '전라북도 김제시 금산면 금산리 39 (54342)', '전북', 6, 182000, '모악산에 위치한 대사찰로, 국보 제62호인 미륵전이 유명합니다. 웅장한 3층 법당 안의 거대한 미륵불을 만나보세요.', 0.0, 0),
('위도', '전라북도 부안군 위도면 (56351)', '전북', 5, 168000, '아름다운 해수욕장과 빼어난 자연경관을 자랑하는 섬입니다. 낚시를 즐기거나 해안도로를 따라 드라이브하기 좋습니다.', 0.0, 0),
('무주 반디랜드', '전라북도 무주군 설천면 무설로 1324 (55555)', '전북', 8, 222000, '청정지역 무주에 서식하는 반딧불이를 주제로 한 테마공원입니다. 곤충박물관과 천문과학관에서 자연의 신비를 체험해보세요.', 0.0, 0),
('전주 남부시장 야시장', '전라북도 전주시 완산구 풍남문2길 63 (55044)', '전북', 7, 188000, '매주 금, 토요일 밤에 열리는 야시장에서 다양한 길거리 음식을 맛보세요. 젊음의 활기와 맛있는 냄새가 가득한 곳입니다.', 0.0, 0),
('새만금방조제', '전라북도 군산시 비응도동 (54000)', '전북', 6, 178000, '세계에서 가장 긴 방조제로, 드라이브 코스로 인기가 많습니다. 광활한 바다와 간척지를 감상하며 달려보세요.', 0.0, 0),
('국립익산박물관', '전라북도 익산시 금마면 미륵사지로 362 (57080)', '전북', 5, 162000, '미륵사지 유적 바로 옆에 위치한 박물관으로, 백제 문화의 정수를 만날 수 있습니다. 미륵사지 출토 유물을 중심으로 전시하고 있습니다.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES
('여수 밤바다', '전라남도 여수시 돌산읍 돌산로 3600-1 (59768)', '전남', 7, 220000, '낭만적인 여수 밤바다를 감상할 수 있는 최적의 장소입니다. 해상케이블카를 타고 거북선대교와 돌산대교의 야경을 즐겨보세요.', 0.0, 0),
('순천만국가정원', '전라남도 순천시 국가정원1호길 47 (57995)', '전남', 8, 240000, '세계 각국의 정원을 아름답게 꾸며놓은 곳입니다. 스카이큐브를 타고 순천만습지까지 이동하며 멋진 풍경을 감상하세요.', 0.0, 0),
('보성 녹차밭', '전라남도 보성군 보성읍 녹차로 763-67 (59441)', '전남', 6, 190000, '초록빛 융단을 깔아놓은 듯한 드넓은 녹차밭이 장관을 이룹니다. 싱그러운 녹차 향기를 맡으며 힐링하고, 맛있는 녹차 아이스크림도 맛보세요.', 0.0, 0),
('담양 죽녹원', '전라남도 담양군 담양읍 죽녹원로 119 (57342)', '전남', 7, 200000, '울창한 대나무 숲길을 걸으며 심신의 안정을 찾을 수 있는 곳입니다. 시원한 대숲 바람을 맞으며 사색의 시간을 가져보세요.', 0.0, 0),
('곡성 기차마을', '전라남도 곡성군 오곡면 기차마을로 232 (57512)', '전남', 8, 210000, '옛 곡성역을 중심으로 조성된 테마파크입니다. 증기기관차를 타고 섬진강변을 달리며 아름다운 풍경을 감상해보세요.', 0.0, 0),
('목포 갓바위', '전라남도 목포시 남농로 166-1 (58721)', '전남', 5, 170000, '두 사람이 삿갓을 쓴 모습처럼 보이는 독특한 모양의 바위입니다. 해상보행교를 따라 걸으며 바다 위에서 갓바위를 감상할 수 있습니다.', 0.0, 0),
('신안 퍼플섬', '전라남도 신안군 안좌면 소곡두리길 257-35 (58811)', '전남', 6, 180000, '마을 전체가 보라색으로 꾸며진 독특하고 아름다운 섬입니다. 보라색 옷을 입고 방문하면 무료로 입장할 수 있습니다.', 0.0, 0),
('해남 땅끝마을', '전라남도 해남군 송지면 땅끝마을길 42 (59042)', '전남', 7, 195000, '우리나라 육지의 최남단으로, 새로운 시작을 다짐하는 의미 있는 장소입니다. 땅끝전망대에 올라 다도해의 아름다운 풍경을 감상하세요.', 0.0, 0),
('구례 화엄사', '전라남도 구례군 마산면 화엄사로 539 (57614)', '전남', 8, 230000, '지리산에 위치한 천년고찰로, 국보 4점을 비롯한 많은 문화재를 보유하고 있습니다. 각황전의 웅장함과 석등의 아름다움을 느껴보세요.', 0.0, 0),
('완도 청산도', '전라남도 완도군 청산면 (59166)', '전남', 6, 205000, '아시아 최초의 슬로시티로 지정된 아름다운 섬입니다. 느리게 걸으며 유채꽃과 청보리밭이 어우러진 그림 같은 풍경을 감상해보세요.', 0.0, 0),
('강진 가우도', '전라남도 강진군 도암면 다산초당길 68-35 (59262)', '전남', 5, 175000, '출렁다리를 건너 들어가는 섬으로, 짚트랙을 타고 바다 위를 날아볼 수 있습니다. 다산초당과 백련사도 함께 둘러보세요.', 0.0, 0),
('진도 울돌목', '전라남도 진도군 군내면 진도대교로 82 (58931)', '전남', 7, 215000, '이순신 장군이 명량해전을 승리로 이끈 역사의 현장입니다. 거센 물살이 소용돌이치는 울돌목을 보며 장군의 위대함을 느껴보세요.', 0.0, 0),
('장흥 편백숲 우드랜드', '전라남도 장흥군 장흥읍 우드랜드길 180 (59324)', '전남', 8, 225000, '울창한 편백나무 숲에서 피톤치드를 마시며 삼림욕을 즐길 수 있습니다. 숲속의 집에서 하룻밤 묵으며 건강한 휴식을 취해보세요.', 0.0, 0),
('영암 월출산', '전라남도 영암군 영암읍 천황사로 280-43 (58421)', '전남', 9, 250000, '웅장한 기암괴석이 아름다운 호남의 명산입니다. 구름다리를 건너며 아찔한 스릴과 함께 멋진 풍경을 감상하세요.', 0.0, 0),
('나주 읍성', '전라남도 나주시 금성관길 1-1 (58264)', '전남', 6, 185000, '고려시대부터 조선시대까지 호남의 중심지였던 나주의 역사를 간직한 곳입니다. 금성관과 나주향교 등을 둘러보세요.', 0.0, 0),
('화순 운주사', '전라남도 화순군 도암면 천불천탑길 91-44 (58122)', '전남', 5, 160000, '천 개의 불상과 천 개의 탑이 있었다는 전설이 깃든 신비로운 사찰입니다. 와불과 독특한 모양의 석탑들이 인상적입니다.', 0.0, 0),
('고흥 나로우주센터', '전라남도 고흥군 봉래면 하반로 490 (59567)', '전남', 7, 218000, '우리나라 최초의 우주발사기지입니다. 우주과학관에서 우주에 대한 꿈을 키우고, 나로호 발사의 감동을 느껴보세요.', 0.0, 0),
('함평 엑스포공원', '전라남도 함평군 함평읍 곤재로 27 (57129)', '전남', 8, 208000, '매년 봄 나비축제가 열리는 곳으로 유명합니다. 나비생태관에서 다양한 종류의 나비를 만나고, 아름다운 꽃밭을 감상하세요.', 0.0, 0),
('영광 백수해안도로', '전라남도 영광군 백수읍 해안로 957 (57055)', '전남', 6, 198000, '우리나라에서 가장 아름다운 드라이브 코스 중 하나로 꼽히는 곳입니다. 서해의 아름다운 일몰을 감상하며 낭만적인 드라이브를 즐겨보세요.', 0.0, 0),
('무안 회산백련지', '전라남도 무안군 일로읍 백련로 333 (58530)', '전남', 7, 188000, '동양 최대의 백련 자생지로, 여름이면 하얀 연꽃이 호수를 가득 메웁니다. 연꽃 축제 기간에 방문하면 다양한 체험을 즐길 수 있습니다.', 0.0, 0),
('순천만습지', '전라남도 순천시 순천만길 513-25 (58021)', '전남', 8, 235000, '세계 5대 연안습지 중 하나로, 아름다운 갈대밭과 S자 물길이 장관을 이룹니다. 용산전망대에서 바라보는 일몰 풍경은 잊을 수 없는 감동을 선사합니다.', 0.0, 0),
('오동도', '전라남도 여수시 수정동 산1-11 (59732)', '전남', 5, 165000, '섬 전체가 동백나무로 뒤덮여 있어 동백섬이라 불립니다. 동백꽃이 만발하는 겨울과 봄에 방문하면 더욱 아름답습니다.', 0.0, 0),
('메타세쿼이아길', '전라남도 담양군 담양읍 학동리 578-4 (57341)', '전남', 6, 192000, '하늘을 향해 곧게 뻗은 메타세쿼이아 나무가 터널을 이루는 아름다운 길입니다. 사계절 내내 다른 매력을 뽐내는 산책 명소입니다.', 0.0, 0),
('섬진강 기차마을', '전라남도 곡성군 오곡면 기차마을로 232 (57512)', '전남', 9, 245000, '증기기관차, 레일바이크, 장미공원 등 다양한 즐길 거리가 있는 곳입니다. 가족, 연인과 함께 즐거운 시간을 보내기에 좋습니다.', 0.0, 0),
('송광사', '전라남도 순천시 송광면 송광사안길 100 (57944)', '전남', 7, 202000, '우리나라 삼보사찰 중 승보사찰로, 많은 고승들을 배출한 유서 깊은 곳입니다. 고즈넉한 산사에서 마음의 평화를 찾아보세요.', 0.0, 0),
('낙안읍성', '전라남도 순천시 낙안면 충민길 30 (57927)', '전남', 8, 212000, '조선시대의 읍성이 원형 그대로 보존되어 있으며, 현재도 주민들이 거주하고 있는 살아있는 민속촌입니다. 초가집과 돌담길이 정겹습니다.', 0.0, 0),
('유달산', '전라남도 목포시 죽교동 (58721)', '전남', 6, 178000, '목포 시내와 다도해를 한눈에 조망할 수 있는 목포의 상징입니다. 케이블카를 타고 쉽게 정상에 올라 멋진 풍경을 감상할 수 있습니다.', 0.0, 0),
('도갑사', '전라남도 영암군 군서면 도갑사로 306 (58437)', '전남', 5, 158000, '월출산 자락에 위치한 고즈넉한 사찰입니다. 국보 제50호인 해탈문과 아름다운 계곡이 유명합니다.', 0.0, 0),
('두륜산', '전라남도 해남군 삼산면 대흥사길 400 (59040)', '전남', 9, 248000, '케이블카를 타고 정상에 오르면 다도해의 아름다운 풍경을 감상할 수 있습니다. 천년고찰 대흥사를 품고 있는 명산입니다.', 0.0, 0),
('다산초당', '전라남도 강진군 도암면 다산초당길 68-35 (59262)', '전남', 4, 148000, '다산 정약용이 유배 생활을 하며 목민심서 등 위대한 저술을 남긴 곳입니다. 다산의 정신을 느끼며 사색의 시간을 가져보세요.', 0.0, 0);
