INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES ('해변의 스테이 제주28호점', '제주특별자치도 서귀포시 안덕면 사계남로 65길 35 (63644)', '제주', 6, 260000.00, '사랑하는 사람들과 잊지 못할 추억을 만들 수 있는 완벽한 공간입니다. 밤하늘의 별을 보며 하루를 마무리하고, 아침에는 새소리와 함께 눈을 떠보세요.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES ('숲속의 하우스 제주29호점', '제주특별자치도 서귀포시 성산읍 일출로 75길 44 (63643)', '제주', 4, 200000.00, '모던하고 감각적인 인테리어로 꾸며져 있어 머무는 내내 특별한 기분을 선사합니다. 주변 관광지와 가까워 여행을 즐기기에도 최적의 위치에 있습니다.', 0.0, 0);
INSERT INTO guesthouses (name, address, region, capacity, price, description, rating_avg, rating_count) VALUES ('언덕 위 산장 제주30호점', '제주특별자치도 제주시 한림읍 한림해안로 85길 50 (63086)', '제주', 7, 290000.00, '오직 당신만을 위한 프라이빗한 독채에서 진정한 쉼을 누려보세요. 깨끗하고 정돈된 시설과 함께 편안한 잠자리를 제공합니다.', 0.0, 0);
