-- 제주 지역 게스트하우스 데이터 (30개)

WITH RECURSIVE numbers AS (
  -- 1부터 시작하는 숫자 시리즈 생성
  SELECT 1 AS n
  UNION ALL
  -- 300이 될 때까지 1씩 증가
  SELECT n + 1 FROM numbers WHERE n < 510
)
-- 생성된 숫자를 기반으로 데이터 선택 및 삽입
SELECT
  n,                -- id
  n,                -- guesthouse_id
  RAND() > 0.5,     -- wifi (true 또는 false)
  RAND() > 0.5,     -- parking
  RAND() > 0.5,     -- breakfast
  RAND() > 0.5,     -- air_conditioner
  RAND() > 0.5,     -- tv
  RAND() > 0.5,     -- laundry
  RAND() > 0.5,     -- kitchen
  RAND() > 0.5      -- pet_allowed
FROM numbers;

